// Get all input items
const items = $input.all();

// Extract emails from all registrants
const emails = [];

for (const item of items) {
  const registrants = item.json.registrants || [];

  for (const registrant of registrants) {
    if (registrant.email) {
      emails.push(registrant.email);
    }
  }
}

// Return as n8n format - one item per email
return emails.map(email => ({
  json: {
    email: email
  }
}));

const crypto = require('crypto');
const email = $('Filter Out Internal Emails').item.json.email.toLowerCase();
const hash = 
  crypto.createHash('md5').update(email).digest('hex');

return { hash: hash };
