// Extract main content from HTML
const input = $('Content Input Form').first().json;
const response = $input.first().json;
const html = response.data || response.body || (typeof response === 'string' ? response : '');

let title = '';
let content = '';
let description = '';
let author = '';

try {
  // Extract title
  const titleMatch = html.match(/<title[^>]*>([^<]+)<\/title>/i);
  const ogTitleMatch = html.match(/<meta[^>]*property=["']og:title["'][^>]*content=["']([^"']+)["']/i);
  title = ogTitleMatch ? ogTitleMatch[1].trim() : (titleMatch ? titleMatch[1].trim() : '');

  // Extract meta description
  const descMatch = html.match(/<meta[^>]*name=["']description["'][^>]*content=["']([^"']+)["']/i) ||
                    html.match(/<meta[^>]*property=["']og:description["'][^>]*content=["']([^"']+)["']/i) ||
                    html.match(/<meta[^>]*content=["']([^"']+)["'][^>]*name=["']description["']/i);
  description = descMatch ? descMatch[1].trim() : '';

  // Extract author
  const authorMatch = html.match(/<meta[^>]*name=["']author["'][^>]*content=["']([^"']+)["']/i);
  author = authorMatch ? authorMatch[1].trim() : '';

  // Remove non-content elements
  let cleanHtml = html
    .replace(/<script[^>]*>[\s\S]*?<\/script>/gi, '')
    .replace(/<style[^>]*>[\s\S]*?<\/style>/gi, '')
    .replace(/<nav[^>]*>[\s\S]*?<\/nav>/gi, '')
    .replace(/<header[^>]*>[\s\S]*?<\/header>/gi, '')
    .replace(/<footer[^>]*>[\s\S]*?<\/footer>/gi, '')
    .replace(/<aside[^>]*>[\s\S]*?<\/aside>/gi, '')
    .replace(/<noscript[^>]*>[\s\S]*?<\/noscript>/gi, '')
    .replace(/<!--[\s\S]*?-->/gi, '');

  // Try to find article content (priority order)
  const articleMatch = cleanHtml.match(/<article[^>]*>([\s\S]*?)<\/article>/i);
  const mainMatch = cleanHtml.match(/<main[^>]*>([\s\S]*?)<\/main>/i);
  const entryMatch = cleanHtml.match(/<div[^>]*class=["'][^"']*(?:entry-content|post-content|article-content|blog-content)[^"']*["'][^>]*>([\s\S]*?)<\/div>/i);
  const contentMatch = cleanHtml.match(/<div[^>]*class=["'][^"']*(?:content|post|article|entry)[^"']*["'][^>]*>([\s\S]*?)<\/div>/i);

  let rawContent = articleMatch ? articleMatch[1] : 
                   mainMatch ? mainMatch[1] : 
                   entryMatch ? entryMatch[1] :
                   contentMatch ? contentMatch[1] : cleanHtml;

  // Strip remaining HTML tags and clean up
  content = rawContent
    .replace(/<h[1-6][^>]*>/gi, '\n\n## ')
    .replace(/<\/h[1-6]>/gi, '\n\n')
    .replace(/<p[^>]*>/gi, '\n')
    .replace(/<\/p>/gi, '\n')
    .replace(/<br[^>]*>/gi, '\n')
    .replace(/<li[^>]*>/gi, '\n‚Ä¢ ')
    .replace(/<[^>]+>/g, ' ')
    .replace(/&nbsp;/g, ' ')
    .replace(/&amp;/g, '&')
    .replace(/&lt;/g, '<')
    .replace(/&gt;/g, '>')
    .replace(/&quot;/g, '"')
    .replace(/&#39;/g, "'")
    .replace(/&hellip;/g, '...')
    .replace(/&mdash;/g, '‚Äî')
    .replace(/&ndash;/g, '‚Äì')
    .replace(/\n\s*\n\s*\n/g, '\n\n')
    .replace(/[ \t]+/g, ' ')
    .trim();

  // Limit content length
  if (content.length > 12000) {
    content = content.substring(0, 12000) + '\n\n[Content truncated...]';
  }

  if (content.length < 100) {
    throw new Error('Could not extract meaningful content from page');
  }

} catch (error) {
  content = 'Error extracting content: ' + error.message;
}

return {
  contentType: 'blog',
  sourceUrl: input['Content Input'].trim(),
  title: title,
  author: author,
  description: description,
  content: content,
  targetAudience: input['Target Audience (Optional)'] || '',
  additionalInstructions: input['Additional Instructions (Optional)'] || '',
  wordCount: content.split(/\s+/).length,
  extractedAt: new Date().toISOString()
};

// Extract YouTube video ID from URL
const input = $('Content Input Form').first().json;
const url = input['Content Input'].trim();

let videoId = '';

// Handle various YouTube URL formats
const patterns = [
  /(?:youtube\.com\/watch\?v=)([^&\s]+)/,
  /(?:youtu\.be\/)([^?\s]+)/,
  /(?:youtube\.com\/embed\/)([^?\s]+)/,
  /(?:youtube\.com\/v\/)([^?\s]+)/,
  /(?:youtube\.com\/shorts\/)([^?\s]+)/,
  /(?:youtube\.com\/live\/)([^?\s]+)/
];

for (const pattern of patterns) {
  const match = url.match(pattern);
  if (match) {
    videoId = match[1].split('&')[0]; // Remove any trailing params
    break;
  }
}

if (!videoId) {
  throw new Error('Could not extract YouTube video ID from URL: ' + url);
}

return {
  videoId: videoId,
  sourceUrl: url,
  targetAudience: input['Target Audience (Optional)'] || '',
  additionalInstructions: input['Additional Instructions (Optional)'] || ''
};

// Process YouTube data from metadata API and page scrape
const videoIdData = $('Extract Video ID').first().json;
const metadataResponse = $('Get YouTube Metadata').first().json;
const pageResponse = $('Fetch YouTube Page').first().json;
const pageHtml = pageResponse.data || pageResponse.body || '';

// Extract video details from API
const video = metadataResponse.items?.[0]?.snippet || {};
const stats = metadataResponse.items?.[0]?.statistics || {};
const contentDetails = metadataResponse.items?.[0]?.contentDetails || {};

const title = video.title || 'Unknown Title';
const description = video.description || '';
const channelTitle = video.channelTitle || '';
const publishedAt = video.publishedAt || '';
const viewCount = stats.viewCount || '';
const likeCount = stats.likeCount || '';

// Try to extract transcript/captions from page
let transcript = '';

try {
  // Look for captions in the page data
  const captionsMatch = pageHtml.match(/"captions":\{"playerCaptionsTracklistRenderer":\{"captionTracks":\[([^\]]+)\]/);

  if (captionsMatch) {
    // Found captions data - extract the URL
    const captionData = captionsMatch[1];
    const baseUrlMatch = captionData.match(/"baseUrl":"([^"]+)"/);

    if (baseUrlMatch) {
      // We found a caption URL - note this for the user
      transcript = '[Captions available - using video metadata for content]';
    }
  }

  // Also try to find auto-generated transcript in ytInitialPlayerResponse
  const playerMatch = pageHtml.match(/ytInitialPlayerResponse\s*=\s*({.+?});/);
  if (playerMatch) {
    try {
      const playerData = JSON.parse(playerMatch[1]);
      if (playerData.captions?.playerCaptionsTracklistRenderer?.captionTracks) {
        transcript = '[Auto-captions detected]';
      }
    } catch (e) {
      // JSON parse failed, continue
    }
  }
} catch (e) {
  // Transcript extraction failed, continue with metadata
}

// Build comprehensive content from available data
let content = `# ${title}\n\n`;
content += `**Channel:** ${channelTitle}\n`;
if (publishedAt) {
  content += `**Published:** ${new Date(publishedAt).toLocaleDateString()}\n`;
}
if (viewCount) {
  content += `**Views:** ${parseInt(viewCount).toLocaleString()}\n`;
}
content += `\n---\n\n`;

if (description) {
  content += `## Video Description\n\n${description}\n\n`;
}

if (transcript) {
  content += `\n---\n\n*Note: ${transcript}*\n`;
}

// Truncate if too long
if (content.length > 12000) {
  content = content.substring(0, 12000) + '\n\n[Content truncated...]';
}

return {
  contentType: 'youtube',
  sourceUrl: videoIdData.sourceUrl,
  videoId: videoIdData.videoId,
  title: title,
  channelTitle: channelTitle,
  description: description.substring(0, 1000),
  content: content,
  viewCount: viewCount,
  likeCount: likeCount,
  publishedAt: publishedAt,
  targetAudience: videoIdData.targetAudience || '',
  additionalInstructions: videoIdData.additionalInstructions || '',
  wordCount: content.split(/\s+/).length,
  extractedAt: new Date().toISOString()
};

// Pass raw text through with metadata
const input = $('Content Input Form').first().json;
const rawText = input['Content Input'].trim();

// Try to extract a title from first line
let title = '';
const lines = rawText.split('\n').filter(l => l.trim());
const firstLine = lines[0] || '';

// Use first line as title if it's short enough and looks like a title
if (firstLine.length < 150 && firstLine.length > 3) {
  // Remove markdown heading markers if present
  title = firstLine.replace(/^#+\s*/, '').trim();
}

let content = rawText;

// Limit content length
if (content.length > 12000) {
  content = content.substring(0, 12000) + '\n\n[Content truncated...]';
}

return {
  contentType: 'raw_text',
  sourceUrl: '',
  title: title || 'User Provided Content',
  description: '',
  content: content,
  targetAudience: input['Target Audience (Optional)'] || '',
  additionalInstructions: input['Additional Instructions (Optional)'] || '',
  wordCount: content.split(/\s+/).length,
  extractedAt: new Date().toISOString()
};

