// Parse AI response and build formatted Google Doc content

// 1) Figure out where the normalized source content came from
let sourceData = null;

function safeGetNodeFirstJson(nodeName) {
  try {
    return $(nodeName).first().json;
  } catch (e) {
    return null;
  }
}

// Try blog path
sourceData = safeGetNodeFirstJson('Extract Blog Content');

// If not blog, try YouTube path
if (!sourceData || !sourceData.contentType) {
  sourceData = safeGetNodeFirstJson('Process YouTube Data');
}

// If not YouTube, try raw text path
if (!sourceData || !sourceData.contentType) {
  sourceData = safeGetNodeFirstJson('Process Raw Text');
}

if (!sourceData) {
  throw new Error('Could not determine source content (blog / YouTube / raw text). Make sure one of the extraction nodes ran successfully.');
}

// 2) Get AI response from previous node
const aiResponse = $input.first().json;

// 3) Parse AI JSON safely
let content;
try {
  if (typeof aiResponse.message?.content === 'string') {
    // Clean any markdown code blocks
    let jsonStr = aiResponse.message.content
      .replace(/```json\n?/g, '')
      .replace(/```\n?/g, '')
      .trim();
    content = JSON.parse(jsonStr);
  } else {
    content = aiResponse.message?.content || aiResponse;
  }
} catch (e) {
  throw new Error(
    'Failed to parse AI response: ' +
      e.message +
      '\n\nRaw response: ' +
      JSON.stringify(aiResponse).substring(0, 500)
  );
}

// 4) Build Google Doc content
const cleanTitle = sourceData.title || 'Repurposed Content';
const docTitle =
  `Repurposed: ${cleanTitle.substring(0, 60)}` +
  (cleanTitle.length > 60 ? '...' : '');

let doc = '';

// Header
doc += `# ${cleanTitle}\n\n`;
doc += `üìÖ Generated: ${new Date().toLocaleDateString('en-US', {
  weekday: 'long',
  year: 'numeric',
  month: 'long',
  day: 'numeric',
})}\n`;

doc += `üìù Source: ${
  sourceData.contentType === 'youtube'
    ? 'YouTube Video'
    : sourceData.contentType === 'blog'
    ? 'Blog Post'
    : 'Raw Text'
}\n`;

if (sourceData.sourceUrl) {
  doc += `üîó URL: ${sourceData.sourceUrl}\n`;
}
doc += `\n---\n\n`;

// Analysis Summary
if (content.analysis) {
  doc += `## üìä Content Analysis\n\n`;
  doc += `**Summary:** ${content.analysis.contentSummary || 'N/A'}\n\n`;
  doc += `**Detected Tone:** ${content.analysis.detectedTone || 'N/A'}\n`;
  doc += `**Target Audience:** ${content.analysis.targetAudience || 'N/A'}\n`;
  doc += `**Key Message:** ${content.analysis.keyMessage || 'N/A'}\n`;
  doc += `**Main Topics:** ${(content.analysis.mainTopics || []).join(', ')}\n\n`;
  doc += `---\n\n`;
}

// LinkedIn Posts
doc += `## üíº LinkedIn Posts\n\n`;
if (content.linkedInPosts && content.linkedInPosts.length > 0) {
  content.linkedInPosts.forEach((post, i) => {
    doc += `### Option ${i + 1}: ${post.style || 'Variation ' + (i + 1)}\n\n`;
    doc += `**HOOK:**\n${post.hook}\n\n`;
    doc += `**BODY:**\n${post.body}\n\n`;
    doc += `**CTA:**\n${post.cta}\n\n`;
    if (post.hashtags && post.hashtags.length > 0) {
      doc += `**Hashtags:** ${post.hashtags.join(' ')}\n\n`;
    }
    doc += `---\n\n`;
  });
} else {
  doc += `*No LinkedIn posts generated*\n\n`;
}

// Twitter Thread
doc += `## üê¶ Twitter/X Thread\n\n`;
if (content.twitterThread?.tweets && content.twitterThread.tweets.length > 0) {
  content.twitterThread.tweets.forEach((tweet, i) => {
    doc += `**${i + 1}/${content.twitterThread.tweets.length}**\n${tweet}\n\n`;
  });
  if (content.twitterThread.altHook) {
    doc += `**üîÑ Alternative Hook (for A/B testing):**\n${content.twitterThread.altHook}\n\n`;
  }
} else {
  doc += `*No Twitter thread generated*\n\n`;
}
doc += `---\n\n`;

// Email Newsletter
doc += `## üìß Email Newsletter\n\n`;
if (content.emailNewsletter) {
  const email = content.emailNewsletter;
  doc += `### Subject Line Options\n`;
  (email.subjectLines || []).forEach((subj, i) => {
    doc += `${i + 1}. ${subj}\n`;
  });
  doc += `\n**Preview Text:** ${email.previewText || ''}\n\n`;
  doc += `### Email Body\n\n`;
  if (email.greeting) doc += `${email.greeting}\n\n`;
  doc += `${email.body || ''}\n\n`;
  if (email.cta) doc += `**CTA:** ${email.cta}\n\n`;
  if (email.signOff) doc += `${email.signOff}\n\n`;
} else {
  doc += `*No email newsletter generated*\n\n`;
}
doc += `---\n\n`;

// Key Takeaways
doc += `## üéØ Key Takeaways\n\n`;
if (content.keyTakeaways && content.keyTakeaways.length > 0) {
  content.keyTakeaways.forEach((takeaway, i) => {
    doc += `${i + 1}. ${takeaway}\n`;
  });
} else {
  doc += `*No takeaways generated*\n`;
}
doc += `\n---\n\n`;

// Quote Cards
doc += `## üí¨ Quote Cards\n\n`;
doc += `*Ready for social media graphics*\n\n`;
if (content.quoteCards && content.quoteCards.length > 0) {
  content.quoteCards.forEach((item) => {
    const quote = typeof item === 'string' ? item : item.quote;
    const context = typeof item === 'object' ? item.context : '';
    doc += `> "${quote}"\n`;
    if (context) doc += `> *‚Äî ${context}*\n`;
    doc += `\n`;
  });
} else {
  doc += `*No quotes generated*\n`;
}
doc += `---\n\n`;

// Video Script
doc += `## üé¨ Short-Form Video Script\n\n`;
if (content.shortFormVideoScript) {
  const script = content.shortFormVideoScript;
  doc += `**Format:** ${script.format || 'Hook ‚Üí Problem ‚Üí Solution ‚Üí CTA'}\n`;
  doc += `**Target Length:** ${script.targetLength || '60 seconds'}\n\n`;

  if (script.sections) {
    doc += `### üé£ HOOK (0-3 sec)\n${script.sections.hook || ''}\n\n`;
    doc += `### ‚ùì PROBLEM (3-12 sec)\n${script.sections.problem || ''}\n\n`;
    doc += `### üí° SOLUTION (12-35 sec)\n${script.sections.solution || ''}\n\n`;
    doc += `### ‚úÖ PROOF (35-50 sec)\n${script.sections.proof || ''}\n\n`;
    doc += `### üëâ CTA (50-60 sec)\n${script.sections.cta || ''}\n\n`;
  }

  if (script.visualNotes) {
    doc += `### üé• Visual Notes\n${script.visualNotes}\n\n`;
  }
} else {
  doc += `*No video script generated*\n`;
}

doc += `---\n\n`;
doc += `*Generated by AI Content Repurposing Engine*\n`;
doc += `*Created by [Agentical AI](https://agenticalai.com)*`;

// 5) Return data for Google Docs node
return {
  docTitle: docTitle,
  docContent: doc,
  sourceTitle: sourceData.title,
  sourceType: sourceData.contentType,
  sourceUrl: sourceData.sourceUrl || null,
  analysis: content.analysis || {},
  generatedAt: new Date().toISOString(),
};

// Prepare final response data
const docData = $('Write Content').first().json;
const prepData = $('Format Document Content').first().json;

const docUrl = `https://docs.google.com/document/d/${docData.documentId}/edit`;

return {
  success: true,
  documentUrl: docUrl,
  documentId: docData.documentId,
  documentTitle: prepData.docTitle,
  sourceType: prepData.sourceType,
  sourceTitle: prepData.sourceTitle,
  sourceUrl: prepData.sourceUrl,
  analysis: prepData.analysis,
  generatedAt: prepData.generatedAt
};
