// ğŸ“¥ Ù…Ø¹Ø§Ù„Ø¬Ø© Ø§Ù„Ø±Ø³Ø§Ø¦Ù„ Ø§Ù„ÙˆØ§Ø±Ø¯Ø© Ù…Ù† WhatsApp API
const g = $getWorkflowStaticData('global');
g.contacts = g.contacts || {};

const body = $json.body || $json;
console.log('ğŸ“¨ Received webhook:', JSON.stringify(body, null, 2));

// Ø§Ù„ØªØ­Ù‚Ù‚ Ù…Ù† Ø§Ù„Ø¨Ù†ÙŠØ©
const entry = body.entry?.[0];
if (!entry) {
  return [{ json: { ok: false, error: 'no_entry' } }];
}

const changes = entry.changes?.[0];
const value = changes?.value;
if (!value || !value.messages) {
  return [{ json: { ok: false, error: 'no_messages' } }];
}

const phoneNumberId = value.metadata?.phone_number_id;
const businessNumber = value.metadata?.display_phone_number || '';
const messages = value.messages;
const outputs = [];

for (const msg of messages) {
  const from = msg.from;
  const msgId = msg.id;
  const timestamp = parseInt(msg.timestamp);
  const msgType = msg.type;

  // Ø§Ø³Ù… Ø§Ù„Ù…Ø±Ø³Ù„
  const contactName = value.contacts?.[0]?.profile?.name || from || 'Ø¹Ù…ÙŠÙ„';
  const chatKey = `${phoneNumberId}:${from}`;

  // âœ… Ø­ÙØ¸ Contact ÙÙ‚Ø· (Ø¨Ø¯ÙˆÙ† Ø±Ø³Ø§Ø¦Ù„)
  if (!g.contacts[chatKey]) {
    g.contacts[chatKey] = {
      platform: 'whatsapp',
      account: phoneNumberId,
      phone_number_id: phoneNumberId,
      peer: from,
      name: contactName
    };
  }

  // âœ… Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø£Ø³Ø§Ø³ÙŠØ© (Ù…ØªÙˆØ§ÙÙ‚Ø© Ù…Ø¹ Ø¨Ø§Ù‚ÙŠ Ø§Ù„Ù€ workflow)
  const baseData = {
    phone_number: from,
    phone_number_id: phoneNumberId,
    business_number: businessNumber,
    contact_name: contactName,
    is_status_update: false,
    message_text: '',
    interactive_reply: '',
    interactive_title: '',
    interactive_type: '',
    media_type: '',
    media_url: '',
    media_id: '',
    media_caption: '',
    media_filename: '',
    media_mime_type: ''
  };

  // Ù…Ø¹Ø§Ù„Ø¬Ø© Ø­Ø³Ø¨ Ø§Ù„Ù†ÙˆØ¹
  if (msgType === 'text') {
    baseData.message_text = msg.text?.body || '';
    outputs.push({ json: baseData });
  }

  // ğŸ“ Document
  else if (msgType === 'document') {
    const doc = msg.document || {};
    const originalFilename = doc.filename || 'document';

    console.log('ğŸ“„ Document received:', {
      id: doc.id,
      filename: originalFilename,
      mime: doc.mime_type,
      caption: doc.caption
    });

    baseData.media_type = 'document';
    baseData.media_id = doc.id;
    baseData.media_mime_type = doc.mime_type || 'application/octet-stream';
    baseData.media_caption = doc.caption || '';
    baseData.media_filename = originalFilename;  // âœ… Ù‡Ù†Ø§!
    baseData.message_text = doc.caption || originalFilename;

    outputs.push({ json: baseData });
  }

  // ğŸ–¼ï¸ Image
  else if (msgType === 'image') {
    const img = msg.image || {};

    baseData.media_type = 'image';
    baseData.media_id = img.id;
    baseData.media_mime_type = img.mime_type || 'image/jpeg';
    baseData.media_caption = img.caption || '';
    baseData.message_text = img.caption || 'ğŸ–¼ï¸ ØµÙˆØ±Ø©';

    outputs.push({ json: baseData });
  }

  // ğŸ¥ Video
  else if (msgType === 'video') {
    const vid = msg.video || {};

    baseData.media_type = 'video';
    baseData.media_id = vid.id;
    baseData.media_mime_type = vid.mime_type || 'video/mp4';
    baseData.media_caption = vid.caption || '';
    baseData.message_text = vid.caption || 'ğŸ¥ ÙÙŠØ¯ÙŠÙˆ';

    outputs.push({ json: baseData });
  }

  // ğŸµ Audio/Voice
  else if (msgType === 'audio' || msgType === 'voice') {
    const aud = msg[msgType] || {};

    baseData.media_type = msgType === 'voice' ? 'voice' : 'audio';
    baseData.media_id = aud.id;
    baseData.media_mime_type = aud.mime_type || 'audio/ogg';
    baseData.message_text = msgType === 'voice' ? 'ğŸ¤ Ø±Ø³Ø§Ù„Ø© ØµÙˆØªÙŠØ©' : 'ğŸµ Ù…Ù„Ù ØµÙˆØªÙŠ';

    outputs.push({ json: baseData });
  }

  // Interactive buttons/lists
  else if (msgType === 'interactive') {
    if (msg.interactive?.list_reply) {
      baseData.interactive_reply = msg.interactive.list_reply.id || '';
      baseData.interactive_title = msg.interactive.list_reply.title || '';
      baseData.interactive_type = 'list_reply';
      baseData.message_text = baseData.interactive_title;
    } else if (msg.interactive?.button_reply) {
      baseData.interactive_reply = msg.interactive.button_reply.id || '';
      baseData.interactive_title = msg.interactive.button_reply.title || '';
      baseData.interactive_type = 'button_reply';
      baseData.message_text = baseData.interactive_title;
    }
    outputs.push({ json: baseData });
  }

  // Ù†Ø³Ø® Ù‚Ø¯ÙŠÙ…Ø© (type=button)
  else if (msgType === 'button' && msg.button) {
    baseData.interactive_title = msg.button.text || '';
    baseData.interactive_reply = (msg.button.payload || baseData.interactive_title || '').trim();
    baseData.interactive_type = 'button';
    baseData.message_text = baseData.interactive_title;
    outputs.push({ json: baseData });
  }
}

console.log(`âœ… Processed ${outputs.length} messages`);

return outputs.length > 0 ? outputs : [{ json: { 
  ok: true, 
  no_media: true,
  phone_number_id: phoneNumberId 
} }];

