// Get the aggregated data from previous node
const aggregatedData = $input.first().json.data;

// Initialize totals
let totalMonthlyCost = 0;
let totalAnnualCost = 0;
let totalResourceCount = 0;
let resourcesWithMissingTags = 0;
let totalResourcesScanned = 0;

// Initialize breakdown structures
const byType = {};
const byRegion = {};
const allExpensiveResources = [];

// Process each resource type
aggregatedData.forEach(item => {
  const resourceType = item.resourceType;
  const region = item.region;

  // Add to totals
  totalMonthlyCost += item.monthlyCost || 0;
  totalAnnualCost += item.annualCost || 0;
  totalResourceCount += item.totalCount || 0;

  // Group by resource type
  if (!byType[resourceType]) {
    byType[resourceType] = { count: 0, cost: 0 };
  }
  byType[resourceType].count += item.totalCount || 0;
  byType[resourceType].cost += item.monthlyCost || 0;

  // Group by region
  if (!byRegion[region]) {
    byRegion[region] = { count: 0, cost: 0 };
  }
  byRegion[region].count += item.totalCount || 0;
  byRegion[region].cost += item.monthlyCost || 0;

  // Process resources for top expensive list
  if (item.resources && Array.isArray(item.resources)) {
    totalResourcesScanned += item.resources.length;

    item.resources.forEach(resource => {
      // Count missing tags
      if (resource.missingTags && resource.missingTags.length > 0) {
        resourcesWithMissingTags++;
      }

      // Add to expensive resources list
      let resourceInfo = {
        type: resourceType,
        cost: resource.monthlyCost || 0,
        region: region
      };

      if (resourceType === 'volumes') {
        resourceInfo.id = resource.volumeId;
        resourceInfo.details = `${resource.size} GB in ${resource.availabilityZone}`;
      } else if (resourceType === 'snapshots') {
        resourceInfo.id = resource.snapshotId;
        resourceInfo.details = `${resource.size} GB, ${resource.ageInDays} days old`;
      } else if (resourceType === 'addresses') {
        resourceInfo.id = resource.publicIp;
        resourceInfo.details = 'Unassociated Elastic IP';
      }

      allExpensiveResources.push(resourceInfo);
    });
  }
});

// Sort and get top 5 most expensive resources
const top5Expensive = allExpensiveResources
  .sort((a, b) => b.cost - a.cost)
  .slice(0, 5);

// Calculate compliance rate
const complianceRate = totalResourcesScanned > 0 
  ? ((totalResourcesScanned - resourcesWithMissingTags) / totalResourcesScanned * 100).toFixed(1)
  : 100;

// Calculate savings opportunity (annual cost)
const savingsOpportunity = totalAnnualCost;

// Build summary statistics
return {
  json: {
    scanDate: new Date().toISOString(),
    scanTime: new Date().toLocaleString('en-US', { 
      timeZone: 'America/New_York',
      dateStyle: 'full',
      timeStyle: 'short'
    }),

    // High-level summary
    summary: {
      totalResourcesFound: totalResourceCount,
      totalResourcesScanned: totalResourcesScanned,
      monthlyCost: parseFloat(totalMonthlyCost.toFixed(2)),
      annualCost: parseFloat(totalAnnualCost.toFixed(2)),
      savingsOpportunity: parseFloat(savingsOpportunity.toFixed(2)),
      resourcesWithMissingTags: resourcesWithMissingTags,
      complianceRate: parseFloat(complianceRate)
    },

    // Breakdown by resource type
    byType: {
      volumes: {
        count: byType['volumes']?.count || 0,
        monthlyCost: parseFloat((byType['volumes']?.cost || 0).toFixed(2))
      },
      snapshots: {
        count: byType['snapshots']?.count || 0,
        monthlyCost: parseFloat((byType['snapshots']?.cost || 0).toFixed(2))
      },
      elasticIPs: {
        count: byType['addresses']?.count || 0,
        monthlyCost: parseFloat((byType['addresses']?.cost || 0).toFixed(2))
      }
    },

    // Breakdown by region
    byRegion: Object.keys(byRegion).map(region => ({
      region: region,
      count: byRegion[region].count,
      monthlyCost: parseFloat(byRegion[region].cost.toFixed(2))
    })),

    // Top 5 most expensive resources
    topExpensive: top5Expensive,

    // Full details for downstream nodes
    fullData: aggregatedData
  }
};

// Get data from Calculate Summary Stats
const data = $input.first().json;
const fullData = data.fullData;

// CSV header
const csvHeader = 'Resource Type,Region,Resource ID,Monthly Cost,Annual Cost,Risk Score,Compliance,Missing Tags,Age (Days),Create Date\n';

// Generate CSV rows from all resources
const csvRows = [];

fullData.forEach(item => {
  const resourceType = item.resourceType;
  const region = item.region;

  if (item.resources && Array.isArray(item.resources)) {
    item.resources.forEach(resource => {
      let row = {};

      // Common fields
      row.resourceType = resourceType;
      row.region = region;
      row.monthlyCost = resource.monthlyCost || 0;
      row.annualCost = (resource.monthlyCost || 0) * 12;
      row.complianceStatus = resource.missingTags?.length === 0 ? 'COMPLIANT' : 'NON-COMPLIANT';
      row.missingTags = resource.missingTags?.join('; ') || 'N/A';

      // Resource-type specific fields
      if (resourceType === 'volumes') {
        row.id = resource.volumeId;
        row.riskScore = resource.monthlyCost > 5 ? 'HIGH' : resource.monthlyCost > 2 ? 'MEDIUM' : 'LOW';
        row.ageDays = 'N/A';
        row.createDate = resource.created || 'N/A';
      } else if (resourceType === 'snapshots') {
        row.id = resource.snapshotId;
        row.riskScore = resource.ageInDays > 365 ? 'HIGH' : resource.ageInDays > 180 ? 'MEDIUM' : 'LOW';
        row.ageDays = resource.ageInDays;
        row.createDate = resource.created || 'N/A';
      } else if (resourceType === 'addresses') {
        row.id = resource.publicIp;
        row.riskScore = 'MEDIUM';
        row.ageDays = 'N/A';
        row.createDate = 'N/A';
      }

      csvRows.push(row);
    });
  }
});

// Convert to CSV string
const csv = csvHeader + csvRows.map(r => 
  `"${r.resourceType}","${r.region}","${r.id}",${r.monthlyCost.toFixed(2)},${r.annualCost.toFixed(2)},"${r.riskScore}","${r.complianceStatus}","${r.missingTags}","${r.ageDays}","${r.createDate}"`
).join('\n');

// Return both CSV string and structured data for Google Sheets
return {
  json: {
    // For potential file attachment
    csv: csv,
    csvFilename: `aws-orphaned-resources-${new Date().toISOString().split('T')[0]}.csv`,

    // For Google Sheets (structured data)
    data: csvRows,

    // Summary for reference
    summary: {
      totalRows: csvRows.length,
      scanDate: data.scanDate,
      totalMonthlyCost: data.summary.monthlyCost,
      totalAnnualCost: data.summary.annualCost
    }
  }
};

const response = $input.item.json.result;
const data = JSON.parse(response.body);

// Calculate snapshot costs (snapshots cost $0.05/GB-month)
const snapshotCostPerGb = 0.05;
const totalSize = data.resources.reduce((sum, snap) => sum + snap.VolumeSize, 0);
const monthlyCost = totalSize * snapshotCostPerGb;
const annualCost = monthlyCost * 12;

// Check for missing tags and age
const requiredTags = ['Environment', 'Owner', 'CostCenter'];
const processedSnapshots = data.resources.map(snap => {
  const existingTags = snap.Tags || [];
  const tagKeys = existingTags.map(t => t.Key);
  const missingTags = requiredTags.filter(tag => !tagKeys.includes(tag));

  // Calculate age in days
  const ageInDays = Math.floor((new Date() - new Date(snap.StartTime)) / (1000 * 60 * 60 * 24));

  return {
    snapshotId: snap.SnapshotId,
    volumeId: snap.VolumeId,
    size: snap.VolumeSize,
    state: snap.State,
    created: snap.StartTime,
    ageInDays: ageInDays,
    description: snap.Description || 'No description',
    missingTags: missingTags,
    monthlyCost: snap.VolumeSize * snapshotCostPerGb
  };
});

return {
  json: {
    region: data.region,
    resourceType: data.resourceType,
    totalCount: data.count,
    totalSize: totalSize,
    monthlyCost: parseFloat(monthlyCost.toFixed(2)),
    annualCost: parseFloat(annualCost.toFixed(2)),
    resources: processedSnapshots
  }
};

const response = $input.item.json.result;
const data = JSON.parse(response.body);

// Calculate monthly cost
const ebsCostPerGb = 0.1;
const totalSize = data.resources.reduce((sum, vol) => sum + vol.Size, 0);
const monthlyCost = totalSize * ebsCostPerGb;
const annualCost = monthlyCost * 12;

// Check for missing tags
const requiredTags = ['Environment', 'Owner', 'CostCenter'];
const resourcesWithMissingTags = data.resources.map(vol => {
  const existingTags = vol.Tags || [];
  const tagKeys = existingTags.map(t => t.Key);
  const missingTags = requiredTags.filter(tag => !tagKeys.includes(tag));

  return {
    volumeId: vol.VolumeId,
    size: vol.Size,
    state: vol.State,
    created: vol.CreateTime,
    availabilityZone: vol.AvailabilityZone,
    missingTags: missingTags,
    monthlyCost: vol.Size * ebsCostPerGb
  };
});

return {
  json: {
    region: data.region,
    resourceType: data.resourceType,
    totalCount: data.count,
    totalSize: totalSize,
    monthlyCost: monthlyCost,
    annualCost: annualCost,
    resources: resourcesWithMissingTags
  }
};

const response = $input.item.json.result;
const data = JSON.parse(response.body);

// Unassociated Elastic IPs cost $3.60/month ($0.005/hour)
const elasticIpCostPerMonth = 3.60;
const totalCount = data.resources.length;
const monthlyCost = totalCount * elasticIpCostPerMonth;
const annualCost = monthlyCost * 12;

// Check for missing tags
const requiredTags = ['Environment', 'Owner', 'CostCenter'];
const processedIPs = data.resources.map(ip => {
  const existingTags = ip.Tags || [];
  const tagKeys = existingTags.map(t => t.Key);
  const missingTags = requiredTags.filter(tag => !tagKeys.includes(tag));

  return {
    allocationId: ip.AllocationId,
    publicIp: ip.PublicIp,
    domain: ip.Domain,
    networkBorderGroup: ip.NetworkBorderGroup,
    missingTags: missingTags,
    monthlyCost: elasticIpCostPerMonth
  };
});

return {
  json: {
    region: data.region,
    resourceType: data.resourceType,
    totalCount: data.count,
    monthlyCost: parseFloat(monthlyCost.toFixed(2)),
    annualCost: parseFloat(annualCost.toFixed(2)),
    resources: processedIPs
  }
};

// Get data from Calculate Summary Stats
const data = $input.first().json;

// Create a single "clean scan" log entry
const scanEntry = {
  scanDate: new Date().toISOString(),
  scanTime: new Date().toLocaleString('en-US', { 
    timeZone: 'America/New_York',
    dateStyle: 'full',
    timeStyle: 'short'
  }),
  region: data.byRegion && data.byRegion.length > 0 ? data.byRegion[0].region : 'us-east-1',
  status: 'CLEAN',
  totalResourcesFound: 0,
  volumesFound: 0,
  snapshotsFound: 0,
  elasticIPsFound: 0,
  monthlyCost: 0.00,
  annualCost: 0.00,
  complianceRate: 100,
  resourcesWithMissingTags: 0,
  notes: 'No orphaned resources detected - all systems compliant'
};

// CSV header for clean scans
const csvHeader = 'Scan Date,Scan Time,Region,Status,Total Resources,Volumes,Snapshots,Elastic IPs,Monthly Cost,Annual Cost,Compliance Rate,Missing Tags,Notes\n';

// CSV row
const csvRow = `"${scanEntry.scanDate}","${scanEntry.scanTime}","${scanEntry.region}","${scanEntry.status}",${scanEntry.totalResourcesFound},${scanEntry.volumesFound},${scanEntry.snapshotsFound},${scanEntry.elasticIPsFound},${scanEntry.monthlyCost.toFixed(2)},${scanEntry.annualCost.toFixed(2)},${scanEntry.complianceRate},${scanEntry.resourcesWithMissingTags},"${scanEntry.notes}"`;

const csv = csvHeader + csvRow;

// Return data for Google Sheets
return {
  json: {
    // For CSV export if needed
    csv: csv,
    csvFilename: `aws-clean-scan-${new Date().toISOString().split('T')[0]}.csv`,

    // Structured data for Google Sheets
    scanDate: scanEntry.scanDate,
    scanTime: scanEntry.scanTime,
    region: scanEntry.region,
    status: scanEntry.status,
    totalResourcesFound: scanEntry.totalResourcesFound,
    volumesFound: scanEntry.volumesFound,
    snapshotsFound: scanEntry.snapshotsFound,
    elasticIPsFound: scanEntry.elasticIPsFound,
    monthlyCost: scanEntry.monthlyCost,
    annualCost: scanEntry.annualCost,
    complianceRate: scanEntry.complianceRate,
    resourcesWithMissingTags: scanEntry.resourcesWithMissingTags,
    notes: scanEntry.notes
  }
};
