// Convert dates from DD/MM/YYYY to Unix timestamp (milliseconds)
const input = $input.first().json;

function dateToUnix(dateStr) {
  if (!dateStr || dateStr.trim() === '') return null;

  // Parse DD/MM/YYYY format
  const parts = dateStr.split('/');
  if (parts.length !== 3) {
    throw new Error(`Invalid date format: ${dateStr}. Expected DD/MM/YYYY`);
  }

  const day = parseInt(parts[0], 10);
  const month = parseInt(parts[1], 10) - 1; // Month is 0-indexed in JavaScript
  const year = parseInt(parts[2], 10);

  // Create date object and convert to Unix timestamp in milliseconds
  const date = new Date(year, month, day);
  return date.getTime();
}

const startTimeUnix = dateToUnix(input['Ad published date From (DD/MM/YYYY)']);
const endTimeUnix = dateToUnix(input['Ad published To (DD/MM/YYYY)']);

// Return all original fields plus converted timestamps
return {
  json: {
    ...input,
    start_time_unix: startTimeUnix,
    end_time_unix: endTimeUnix
  }
};

// Build customBody JSON with conditional resultsLimit
const input = $input.first().json;

// Get adv_biz_ids from input
const advBizIds = input.adv_biz_ids || '';

// Build URL with conditional adv_biz_ids parameter
let url = `https://library.tiktok.com/ads?region=${input['Ad target country']}&start_time=${input.start_time_unix}&end_time=${input.end_time_unix}&adv_name=${input['Advertiser name or keyword']}`;

// Add adv_biz_ids only if it's not empty
if (advBizIds && advBizIds.trim() !== '') {
  url += `&adv_biz_ids=${advBizIds}`;
} else {
  url += `&adv_biz_ids=`;
}

url += `&query_type=1&sort_type=last_shown_date,desc`;

// Build the base object
const body = {
  skipDetails: false,
  startUrls: [
    {
      url: url
    }
  ]
};

// Add resultsLimit only if Ad limit is not empty
if (input['Ad limit'] && input['Ad limit'].trim() !== '') {
  const limit = parseInt(input['Ad limit'], 10);
  if (!isNaN(limit) && limit > 0) {
    body.resultsLimit = limit;
  }
}

// Return the JSON string for customBody
return {
  json: {
    ...input,
    customBody: JSON.stringify(body, null, 2)
  }
};

// Safely extract video data and prepare for Google Sheets
// Process all input items
const items = $input.all();

return items.map(item => {
  const input = item.json;

  // Safely get first video URL
  const videoUrl = (input.videos && Array.isArray(input.videos) && input.videos.length > 0 && input.videos[0].url) 
    ? input.videos[0].url 
    : '';

  // Safely get cover image URL
  const coverImageUrl = (input.videos && Array.isArray(input.videos) && input.videos.length > 0 && input.videos[0].coverImageUrl) 
    ? input.videos[0].coverImageUrl 
    : '';

  // Safely get tiktokUser username
  const tiktokUsername = (input.tiktokUser && input.tiktokUser.username) 
    ? input.tiktokUser.username 
    : '';

  // Return all original data plus safely extracted fields
  return {
    json: {
      ...input,
      videoUrl: videoUrl,
      coverImageUrl: coverImageUrl,
      tiktokUsername: tiktokUsername
    }
  };
});

// Collect existing IDs from Google Sheets
const items = $input.all();

const ids = items
  .map(item => item.json.adId)
  // Cast all values to strings to avoid mismatches like "123" vs 123
  .map(id => id != null ? String(id) : undefined)
  .filter((id, index, arr) => id && arr.indexOf(id) === index);

return [{ json: { existingIds: ids } }];

// Filter new ads that don't exist in Google Sheets
const items = $input.all();
const input = items[0].json;

// Get existing IDs from merged data
const existingIds = (input.existingIds || []).map(id => id != null ? String(id) : undefined).filter(Boolean);
const seen = new Set(existingIds);

// Get all ads from items (skip first item which contains existingIds)
const ads = items.slice(1).map(item => item.json);
const newAds = [];

for (const ad of ads) {
  if (!ad?.adId) continue;
  const adId = String(ad.adId);
  if (!seen.has(adId)) {
    newAds.push(ad);
    seen.add(adId); // protect against duplicates within a single run
  }
}

// Return new ads as separate items
return newAds.map(ad => ({ json: ad }));

// Count new ads
const items = $input.all();
return [{
  json: {
    newCount: items.length
  }
}];
