parameters.jsCode:
// 1. Get AI node output (text)
const content = $input.first().json.text;

// 2. Remove markdown code blocks (```json) to extract content
// Extract JSON portion from first { to last }
const jsonString = content.replace(/```json/g, '').replace(/```/g, '').trim();

// 3. Extract JSON object using regex as a fallback safety measure
const jsonMatch = jsonString.match(/\{[\s\S]*\}/);
const cleanJson = jsonMatch ? jsonMatch[0] : jsonString;

// 4. Parse string to JSON object
try {
  return JSON.parse(cleanJson);
} catch (error) {
  // Throw error if parse fails
  throw new Error("JSON parse failed. AI output is not valid JSON: " + cleanJson);
}
