// Build an array of transit authority URLs based on request body
const body = $json;
const routes = body.routes || ['RedLine', 'BlueLine'];
const baseUrls = {
  RedLine: 'https://www.mbta.com/schedules/Red',
  BlueLine: 'https://www.mbta.com/schedules/Blue'
};

return routes.map(route => ({
  json: {
    route,
    url: baseUrls[route] || ''
  }
}));

// Normalize field names and remove duplicate line entries
const items = $input.all();
const seen = new Set();
const output = [];
items.forEach(item => {
  const data = item.json;
  const key = data.line_name || data.route;
  if (!seen.has(key)) {
    seen.add(key);
    output.push({
      json: {
        ...data,
        checked_at: new Date().toISOString()
      }
    });
  }
});
return output;
