// [Details-All] URL COLLECTOR
// Collects URLs from Products, Articles, or Jobs

const staticData = $getWorkflowStaticData('global');

// 1. Initialize Memory
if (!staticData.allItemUrls) {
  staticData.allItemUrls = [];
  staticData.visitedPages = [];
}

// 2. Get Data from HTTP Node
const response = $input.first().json;

// --- DYNAMIC DETECTION START ---
// Detect which navigation type was returned by Zyte
let navObject = null;
if (response.productNavigation) navObject = response.productNavigation;
else if (response.articleNavigation) navObject = response.articleNavigation;
else if (response.jobPostingNavigation) navObject = response.jobPostingNavigation;
else if (response.forumThreadNavigation) navObject = response.forumThreadNavigation;

const items = navObject ? navObject.items : [];
const nextPage = navObject ? navObject.nextPage : null;
// --- DYNAMIC DETECTION END ---

// 3. Extract JUST the URLs
if (items && items.length > 0) {
  const urls = items.map(item => ({ url: item.url }));
  staticData.allItemUrls.push(...urls);
}

// 4. Navigation Logic
let nextUrl = null;
let stop = false;

if (nextPage && nextPage.url) {
  if (staticData.visitedPages.includes(nextPage.url)) {
    stop = true;
  } else {
    // Mark current page as visited
    staticData.visitedPages.push(response.url);

    // --- PRODUCTION CODE ---
    nextUrl = nextPage.url; 

    // --- TEST CODE (Uncomment to limit pages) ---
    // if (staticData.visitedPages.length >= 2) { stop = true; } 
    // else { nextUrl = nextPage.url; }
  }
} else {
  stop = true;
}

return {
  json: {
    stop: stop,
    nextPageUrl: nextUrl,
    totalUrlsCollected: staticData.allItemUrls.length
  }
};

const staticData = $getWorkflowStaticData('global');
const allUrls = staticData.allItemUrls || [];

// Reset the static data so next run starts fresh
staticData.allItemUrls = [];
staticData.visitedPages = [];

// Output the items so the next Loop node sees them!
return allUrls.map(u => ({ json: { url: u.url } }));

// [Details-All] ACCUMULATOR
// Saves detailed data (Product, Article, Job, etc.) inside the loop

const staticData = $getWorkflowStaticData('global');

// 1. Initialize memory
if (!staticData.finalResults) {
  staticData.finalResults = [];
}

// 2. Get Input Items
const items = $input.all();
const validBatch = [];

// 3. Process Items
for (const item of items) {

  // Option A: Success - Check for ANY valid Zyte schema
  if (item.json.product) {
    validBatch.push(item.json.product);
  }
  else if (item.json.article) {
    validBatch.push(item.json.article);
  }
  else if (item.json.jobPosting) {
    validBatch.push(item.json.jobPosting);
  }

  // Option B: Success (Fallback - General browserHtml or Flat JSON)
  // We check that it has NO error message
  else if (item.json && !item.json.error && !item.json.message) {
    validBatch.push(item.json);
  }

  // Option C: FAILURE (Save an Error Record)
  else {
    validBatch.push({
      name: "SKIPPED - ERROR",
      url: "See error details",
      error_message: item.json.message || item.json.error || "Unknown API Error",
      timestamp: new Date().toISOString()
    });
  }
}

// 4. Push to memory
if (validBatch.length > 0) {
  staticData.finalResults.push(...validBatch);
}

// 5. Pass data through
return items;

// COMBINED RETRIEVER & FORMATTER
const staticData = $getWorkflowStaticData('global');

// 1. Retrieve everything we saved in the Accumulator
// If nothing is there, default to an empty array
const allItems = staticData.finalResults || [];

// 2. Reset memory for the next run (Cleanup)
// This ensures your next execution starts with a fresh bag
staticData.finalResults = [];

// 3. Filter the data to remove nulls or bad values
// (Safety check from your second code block)
const validItems = allItems.filter(item => item && typeof item === 'object');

// 4. Map the valid items directly to n8n format for the CSV node
return validItems.map(item => {
  return {
    json: item
  };
});

// ZYTE CONFIG GENERATOR
// Translates Form Inputs into clean Variables

// --- MAPPINGS ---
const categoryMap = {
    "Online Store / Product (E-commerce)": "product",
    "News, Blog, Forum or Article Site": "article",
    "Job Board / Career Site": "job"
};

const goalMap = {
    // 1. Single Item
    "Scrape details of a SINGLE item": "single",
    "Scrape details of a SINGLE job post": "single",
    "Scrape content of a SINGLE article": "single",

    // 2. List (Current Page only)
    "Get List of items from THIS PAGE only": "list",

    // 3. Crawl List (URLs from ALL pages)
    "Get List of items from all pages": "crawl_list",
    "Get List of Job URLs from ALL pages": "crawl_list",
    "Get List of Article URLs from ALL pages": "crawl_list",

    // 4. Details (Current Page - Items on this page)
    "Scrape details of all items on THIS page": "details_current",
    "Scrape content of articles on THIS page": "details_current",

    // 5. Crawl Details (Full Scrape - ALL pages)
    "Scrape details of all items on ALL pages": "crawl_all",
    "Scrape content of articles on ALL pages": "crawl_all",
    "Scrape job details from ALL pages": "crawl_all"
};

// 1. Get Inputs
const rawCategory = $input.first().json["Site Type"];
const rawGoal = $input.first().json["What is your extraction goal?"];
const url = $input.first().json.URL;

// 2. Map to Short Keys
const category = categoryMap[rawCategory] || 'product';
const goal = goalMap[rawGoal] || 'single';

// 3. Determine Base Key
let baseKey = 'product'; 
if (category === 'article') baseKey = 'article';
if (category === 'job') baseKey = 'jobPosting';

// 4. LOGIC ENGINE: Set Target and Navigation Schemas
let targetSchema = baseKey;       // What data do we want?
let navigationSchema = null;      // How do we find the next page?

// --- SCENARIO 1: SINGLE ITEM ---
if (goal === 'single') {
    targetSchema = baseKey;       // e.g. 'product'
    navigationSchema = null;
}

// --- SCENARIO 2: LIST (THIS PAGE ONLY) ---
else if (goal === 'list') {
    // Products/Articles use 'List'. Jobs use 'Navigation'.
    if (baseKey === 'jobPosting') {
        targetSchema = 'jobPostingNavigation';
    } else {
        targetSchema = baseKey + 'List'; // e.g. 'productList'
    }
    navigationSchema = null;
}

// --- SCENARIO 3: CRAWL LIST (ALL PAGES) ---
// CORRECTION APPLIED HERE
else if (goal === 'crawl_list') {
    // TARGET: We want rich list data (same as Scenario 2)
    if (baseKey === 'jobPosting') {
        targetSchema = 'jobPostingNavigation';
    } else {
        targetSchema = baseKey + 'List'; // e.g. 'productList'
    }

    // NAVIGATION: We need to traverse pages to find the next list
    navigationSchema = baseKey + 'Navigation'; 
}

// --- SCENARIO 4 & 5: DETAILS (THIS PAGE OR ALL PAGES) ---
else if (goal === 'details_current' || goal === 'crawl_all') {
    // TARGET: We want full details per item (e.g. 'product')
    targetSchema = baseKey; 

    // NAVIGATION: We need to find the item links first
    navigationSchema = baseKey + 'Navigation'; 
}

// 5. Output
return {
    json: {
        url: url,
        extraction_goal: goal,       // For Switch Node
        target_schema: targetSchema, // For Data Extraction (e.g. productList)
        navigation_schema: navigationSchema, // For Loop Control (e.g. productNavigation)
        // Debugging
        raw_goal: rawGoal 
    }
};

// [List-All] ACCUMULATOR (Universal)
// Saves the batch of items found on the current page to memory
// Works for Products, Articles, and Jobs

const staticData = $getWorkflowStaticData('global');

// 1. Initialize Backpack
if (!staticData.backpack) {
    staticData.backpack = [];
}

// 2. Get Data from the HTTP List Node
const response = $input.first().json;

// 3. DYNAMIC EXTRACTION
// We check for all possible list types Zyte might return for our supported categories
let newItems = [];

if (response.productList && response.productList.products) {
    newItems = response.productList.products;
} 
else if (response.articleList && response.articleList.articles) {
    newItems = response.articleList.articles;
}
else if (response.jobPostingNavigation && response.jobPostingNavigation.items) {
    // Jobs usually return 'items' inside navigation schema
    newItems = response.jobPostingNavigation.items;
}
// Fallbacks for variations in Zyte API responses
else if (response.items) {
    newItems = response.items;
}
else if (response.products) {
    newItems = response.products;
}

// 4. Save to Memory (Safety Check included)
if (newItems.length > 0) {
    staticData.backpack.push(...newItems);
}

// 5. Pass through (Output Status)
return {
    json: {
        status: "Saved",
        type_detected: Object.keys(response).find(k => k.includes('List') || k.includes('Navigation')) || "unknown",
        items_found_on_page: newItems.length,
        total_items_collected: staticData.backpack.length
    }
};

// [List-All] PAGE CONTROLLER (The Brain)
// Handles loop logic for Products, Articles, and Jobs automatically

const staticData = $getWorkflowStaticData('global');

// 1. Initialize Memory
if (!staticData.visited) staticData.visited = [];

// 2. Get Navigation Data from the previous HTTP Node
// Ensure the node name inside $() matches your actual HTTP node name!
const navData = $('HTTP Node: [List-All] Get Page URLs').first().json;
const currentUrl = navData.url; 

// --- DYNAMIC NAVIGATION DETECTION ---
// We check which navigation object exists in the response
let navObject = null;

if (navData.productNavigation) {
    navObject = navData.productNavigation;
} else if (navData.articleNavigation) {
    navObject = navData.articleNavigation;
} else if (navData.jobPostingNavigation) {
    navObject = navData.jobPostingNavigation;
} else if (navData.forumThreadNavigation) { // Rare, but possible
    navObject = navData.forumThreadNavigation; 
}

// Safely get the nextPage object (if it exists)
const nextPageObj = navObject ? navObject.nextPage : null;
// ------------------------------------

// 3. Mark Current Page as Visited
if (currentUrl) {
    staticData.visited.push(currentUrl);
}

// 4. Determine Loop Logic
let nextUrl = null;
let stop = false;

if (nextPageObj && nextPageObj.url) {
    // CRITICAL FIX: The "Infinite Loop" Preventer
    if (staticData.visited.includes(nextPageObj.url)) {
        stop = true; // We have been here before! Stop.
    } else {
        nextUrl = nextPageObj.url; // New page found. Continue.
    }
} else {
    stop = true; // No next page (Null). Stop.
}

// 5. Output Configuration
return {
    json: {
        // Tells the NEXT node (List Data) what to scrape right now
        currentScrapeUrl: currentUrl, 

        // Tells the LOOP (Edit Fields) where to go next
        nextLoopUrl: nextUrl,

        // Tells the IF node whether to continue or finish
        stopLoop: stop
    }
};

// [List-All] FINAL OUTPUT
const staticData = $getWorkflowStaticData('global');

// 1. Retrieve Data
const allProducts = staticData.backpack || [];

// 2. CLEANUP
staticData.backpack = [];
staticData.visited = [];

// 3. SAFETY FILTER (New Optimization)
// Removes nulls or bad data before sending to CSV
const validProducts = allProducts.filter(item => item && typeof item === 'object');

// 4. Format
return validProducts.map(item => {
    return {
        json: item
    };
});
