// Normalise and score impact
const keywordRegex = /(effective immediately|compliance deadline|penalty|must comply|urgent)/i;
const results = [];
$input.all().forEach(item => {
  const data = item.json;
  const highImpact = keywordRegex.test(data.summary || '') || (data.impactLevel || '').toLowerCase() === 'high';
  results.push({
    json: {
      title: data.title || 'Untitled',
      summary: data.summary || '',
      date: data.date || new Date().toISOString(),
      link: data.link || '',
      impactLevel: data.impactLevel || (highImpact ? 'High' : 'Normal'),
      highImpact
    }
  });
});
return results;
