// Get input array
const itemsList = items;

// Convert each wrapped JSON string into a usable object
const cleaned = itemsList.map(item => {
  let raw = item.json.output;

  // Remove ```json block formatting
  raw = raw.replace(/```json|```/g, "").trim();

  // Parse to actual JSON
  const data = JSON.parse(raw);

  return { json: data };
});

// Return cleaned objects
return cleaned;

// n8n Code/Function node - extract cleaned domains from various input shapes

function addFromUrlString(urlStr, domainSet) {
  if (!urlStr || typeof urlStr !== 'string') return;
  urlStr = urlStr.trim();
  urlStr = urlStr.replace(/<[^>]*>/g, ''); // remove HTML tags
  try {
    const u = urlStr.startsWith('http://') || urlStr.startsWith('https://')
      ? new URL(urlStr)
      : new URL('https://' + urlStr);
    let host = u.hostname.toLowerCase();
    if (host.startsWith('www.')) host = host.slice(4);
    if (host) domainSet.add(host);
  } catch (e) {
    const m = urlStr.match(/([a-z0-9.-]+\.[a-z]{2,})(\/|$)/i);
    if (m && m[1]) {
      let host = m[1].toLowerCase();
      if (host.startsWith('www.')) host = host.slice(4);
      domainSet.add(host);
    }
  }
}

function processEntry(entry, domainSet) {
  if (!entry || typeof entry !== 'object') return;
  if (entry.link) addFromUrlString(entry.link, domainSet);
  if (entry.formattedUrl) addFromUrlString(entry.formattedUrl, domainSet);
  if (entry.displayLink) addFromUrlString(entry.displayLink, domainSet);

  if (Array.isArray(entry.items)) {
    entry.items.forEach(it => {
      if (it.link) addFromUrlString(it.link, domainSet);
      if (it.formattedUrl) addFromUrlString(it.formattedUrl, domainSet);
      if (it.displayLink) addFromUrlString(it.displayLink, domainSet);
    });
  }
}

// MAIN: iterate over each incoming n8n item
return items.map(item => {
  const domains = new Set();
  const j = item.json;

  if (Array.isArray(j.data)) {
    j.data.forEach(obj => processEntry(obj, domains));
  } else if (Array.isArray(j.items)) {
    j.items.forEach(obj => processEntry(obj, domains));
  } else if (j.link || j.displayLink || j.formattedUrl) {
    processEntry(j, domains);
  } else if (Array.isArray(j)) {
    j.forEach(obj => processEntry(obj, domains));
  } else {
    Object.keys(j || {}).forEach(k => {
      const v = j[k];
      if (Array.isArray(v)) v.forEach(obj => processEntry(obj, domains));
    });
  }

  // return one item per original input item
  return { json: { domains: Array.from(domains) } };
});

