const html = $json["data"] || $json["body"] || "";

// Strip script/style tags and HTML tags
const cleaned = html
  .replace(/<script[\s\S]*?<\/script>/gi, "")
  .replace(/<style[\s\S]*?<\/style>/gi, "")
  .replace(/<\/?[^>]+(>|$)/g, " ")
  .replace(/\s+/g, " ")
  .trim();

return {
  cleaned_text: cleaned,
  school: $json.school,
  url: $json.url
};

