let txt = $json.content.parts[0].text || "";

txt = txt.replace(/```json/gi, "")
         .replace(/```/g, "")
         .trim();

let parsed;

try {
  parsed = JSON.parse(txt);
} catch (e) {
  parsed = {
    admissions_open: "no",
    reason: "Gemini JSON parse failed",
    raw: txt
  };
}

return parsed;

