const globals = $('GlobalsN').item.json;
let eventData = $json;
let output = {};

//Task id
output.id = eventData.properties['TodoistID'].rich_text.length > 0 ?  eventData.properties['TodoistID'].rich_text[0].text.content : "";
//Task id


//Task Name
output.content =
eventData.properties['Task Name'].title.length > 0 ? eventData.properties['Task Name'].title[0].text.content :
'[empty]';
//Task Name

//Priority
const priorityMap = {
  "1": "4",
  "2": "3",
  "3": "2",
  "4": "1"
};
const priorityKey = String(eventData.priority);
output.priority = priorityMap.hasOwnProperty(priorityKey) ? priorityMap[priorityKey] : "1";
//Priority

//Section
for (let i = 0; i < globals.sections.length; i++) { 
  if (eventData.properties.Section.select.name == ("\n"+ globals.sections[i].Section_name + "\n\n\n") || (globals.sections[i].Section_name) ){
    output.section_id = globals.sections[i].Section_id;
    break;
  }
}
//Section

//Completed
if(eventData.properties.Status.checkbox == false){
  output.checked = false;
}else if(eventData.properties.Status.checkbox == true){
  output.checked=true;
}
//Completed


//Date
if(eventData.properties.Due.date != null){
  const startDate = eventData.properties.Due.date.start;
  output.due_datetime = startDate;
//Recurrence
    if(eventData.properties.Recurrence.rich_text.length !== 0){
      if(eventData.properties.Due.date.start.includes('T')){
        const time = startDate.split('T')[1].substring(0, 5);
        const hours = time.substring(0,2);
        const minutes = time.substring(2,5);
        const ampm = +hours >= 12 ? 'pm' : 'am';
        const hour12 = +hours % 12 === 0 ? 12 : hours % 12;
        output.due_string= eventData.properties.Recurrence.rich_text[0].text.content+ ` at ${hour12}${minutes} ${ampm}`; 
        }else{
      output.due_string = eventData.properties.Recurrence.rich_text[0].text.content;
      }
    }
//Recurrence
//End Date
    if(eventData.properties.Due.date.end !== null){
      output.due_string = eventData.properties.Recurrence.rich_text[0].text.content+ " until " + eventData.properties.Due.date.end.split('T')[0];
        }
    }else{
    // API requires due_string instead of due_datetime when date/time is removed
    output.due_string = "no date";
    }



//Reminder
if (output.due_datetime) {
  output.reminders = [
    { type: "push", minutes_before: 0 }
  ];
}
//Reminder

return { json: output };

const staticData = $getWorkflowStaticData('global');
$input.item.json.clientID = staticData.clientID;
$input.item.json.clientSecret = staticData.clientSecret;
$input.item.json.state = staticData.state;

return $input.item;

let dropDownValues = [];

for (const item of $input.all()) {
  if (item.json.name == "Inbox") continue;
  dropDownValues.push({"option": item.json.name});
}

return { "options": JSON.stringify(dropDownValues) };

let dropDownValues = [];
const results =  $input.first().json.results;
for (const project of results) {
  dropDownValues.push({ option: project.name});
}

return { options: JSON.stringify(dropDownValues)};

let sections = [];
const results = $('Get sections1').first().json.results;
for (const item of results) {
  sections.push({ Section_id: item.id, Section_name: item.name }); 
}

return { json: 
{
  "database_id": $('Get Notion Database ID1').first().json.database_id,
  "project_id": $('Get Todoist Project ID1').first().json.project_id,
  "sections": sections,
  "timezone": $input.first().json['Select your Timezone']
}  
};

let database_id = null;

for (const item of $('Get Notion Databases1').all()) {
  if (item.json.name == $('Choose Notion Database1').first().json['Select Notion Database']) {
    database_id = item.json.id;
  }
}

return { json: { database_id: database_id } };

let project_id = null;
const results = $('Get projects1').first().json.results;
for (const item of results) {
  if (item.name == $('Choose Todoist Project1').first().json['Select Todoist Project']) {
    project_id = item.id;
  }
}

return { json: { project_id: project_id } };

