      margin: 0;
      font-size: 14px;
    }

    .info-grid {
      display: grid;
      grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
      gap: 20px;
      margin-bottom: 35px;
    }

    .info-card {
      background: linear-gradient(135deg, #f8fafc 0%, #f1f5f9 100%);
      padding: 20px;
      border-radius: 16px;
      border: 1px solid #e2e8f0;
      transition: transform 0.2s, box-shadow 0.2s;
    }

    .info-card:hover {
      transform: translateY(-2px);
      box-shadow: 0 10px 20px -5px rgba(0, 0, 0, 0.1);
    }

    .info-label {
      font-size: 12px;
      text-transform: uppercase;
      letter-spacing: 1px;
      color: #64748b;
      font-weight: 600;
      margin-bottom: 8px;
    }

    .info-value {
      font-size: 16px;
      color: #0f172a;
      font-weight: 600;
      word-break: break-word;
    }

    .section {
      margin: 35px 0;
      padding: 30px;
      background: #ffffff;
      border-radius: 16px;
      border: 2px solid #f1f5f9;
      transition: border-color 0.3s;
    }

    .section:hover {
      border-color: #cbd5e1;
    }

    .section-title {
