      border-color: #10b981;
    }

    .actions-title {
      font-size: 18px;
      font-weight: 700;
      color: #0c4a6e;
      margin-bottom: 15px;
      display: flex;
      align-items: center;
      gap: 10px;
    }

    .actions-box.long-term .actions-title {
      color: #064e3b;
    }

