// Get the OpenAI response
const openAIOutput = $input.first().json;

// The actual AI response is nested in: output[0].content[0].text
const aiResponseText = openAIOutput.output[0].content[0].text;

// Parse the JSON string
const aiAnalysis = JSON.parse(aiResponseText);

// Get the original incident data from the previous nodes
// This assumes the webhook data is still in the item
const originalData = $('Normalize Data').first().json;

// Combine everything together
return [{
  json: {
    ...originalData, // All the original incident data
    root_cause: aiAnalysis.root_cause,
    impact_assessment: aiAnalysis.impact_assessment,
    immediate_actions: aiAnalysis.immediate_actions,
    long_term_actions: aiAnalysis.long_term_actions
  }
}];

