// Extract ClickUp task details safely
const task = $json;

// Handle cases where arrays or fields might be empty
const assignee = (task.assignees && task.assignees.length > 0) ? task.assignees[0] : {};
const customFields = task.custom_fields || [];

// Try to find the first field with a value (like a GitHub or docs link)
const linkField = customFields.find(f => f.value) || {};

return [
  {
    json: {
      title: task.name || "Untitled Task",
      description: task.description || "No description available.",
      status: task.status?.status || "No status",
      priority: task.priority?.priority || "Not set",
      due_date: task.due_date || "No due date",
      assignee: assignee.username || "Unassigned",
      email: assignee.email || "No email",
      link: linkField.value || "No link provided",
      url: task.url || "No URL",
    },
  },
];

