// The AI node returns metadata as a STRING inside output.
// Example: "{ \"risk_level\": \"medium\", ... }"

// Grab the raw string from the previous node
const rawString = $json.output;

// Safely parse it into a real JSON object
let parsed = {};
try {
  parsed = JSON.parse(rawString);
} catch (error) {
  // If parsing fails, return an error structure
  parsed = {
    error: "Failed to parse AI metadata JSON",
    raw_output: rawString
  };
}

// Return as proper JSON to the next node
return [
  {
    json: parsed
  }
];

// Input 0 = Task Details from "Parse Task Details"
// Input 1 = Metadata from "Code (Parsed Metadata)"
const task = items[0].json;
const meta = items[1].json;

// Merge both objects into a single final object
const combined = {
  ...task,   // expands all task fields
  ...meta    // expands all metadata fields
};

// Return the combined object as one item
return [{ json: combined }];

// The webhook sends the body as a string like:
// "{\"task_id\":\"86b78ggjw\"}"

// Step 1: Read the raw body
const rawBody = $json.body;

// Step 2: Parse it into actual JSON
let parsed;
try {
  parsed = JSON.parse(rawBody);
} catch (err) {
  parsed = { error: "Invalid JSON", raw: rawBody };
}

// Step 3: Return only the task_id in clean form
return [
  {
    json: {
      task_id: parsed.task_id || null
    }
  }
];
