// Extract data from webhook body
const webhookData = $input.item.json.body;

// Calculate average score from all performance metrics
const scores = webhookData.scores;
const averageScore = (
  scores.technical + 
  scores.communication + 
  scores.leadership + 
  scores.productivity + 
  scores.teamwork
) / 5;

// Determine performance level based on overall rating
const getPerformanceLevel = (rating) => {
  if (rating >= 4.5) return 'Outstanding';
  if (rating >= 4.0) return 'Exceeds Expectations';
  if (rating >= 3.0) return 'Meets Expectations';
  return 'Needs Improvement';
};

// Determine performance color for visual elements
const getPerformanceColor = (rating) => {
  if (rating >= 4.5) return '#4CAF50'; // Green
  if (rating >= 4.0) return '#2196F3'; // Blue
  if (rating >= 3.0) return '#FF9800'; // Orange
  return '#F44336'; // Red
};

// Format date for review period
const currentDate = new Date().toLocaleDateString('en-US', {
  year: 'numeric',
  month: 'long',
  day: 'numeric'
});

// Build comprehensive output object
const output = {
  // Employee Information
  employeeId: webhookData.employeeId,
  employeeName: webhookData.employeeName,
  employeeEmail: webhookData.employeeEmail,

  // Manager Information
  managerName: webhookData.managerName,
  managerEmail: webhookData.managerEmail,

  // Department & Period
  department: webhookData.department,
  reviewPeriod: webhookData.reviewPeriod,
  reviewDate: currentDate,

  // Performance Scores (Individual)
  technicalScore: scores.technical,
  communicationScore: scores.communication,
  leadershipScore: scores.leadership,
  productivityScore: scores.productivity,
  teamworkScore: scores.teamwork,

  // Calculated Metrics
  overallRating: webhookData.overallRating,
  averageScore: parseFloat(averageScore.toFixed(2)),
  performanceLevel: getPerformanceLevel(webhookData.overallRating),
  performanceColor: getPerformanceColor(webhookData.overallRating),

  // Percentage calculations for visual bars
  technicalPercent: (scores.technical / 5) * 100,
  communicationPercent: (scores.communication / 5) * 100,
  leadershipPercent: (scores.leadership / 5) * 100,
  productivityPercent: (scores.productivity / 5) * 100,
  teamworkPercent: (scores.teamwork / 5) * 100,

  // Feedback Details
  strengthsFeedback: webhookData.feedback.strengths,
  improvementsFeedback: webhookData.feedback.improvements,
  managerComments: webhookData.feedback.managerComments,

  // Flags for conditional logic
  isLowPerformer: webhookData.overallRating < 3.0,
  isHighPerformer: webhookData.overallRating >= 4.5,
  requiresManagerAlert: webhookData.overallRating < 3.0,

  // Additional metadata
  scoresObject: scores, // Keep original scores object for reference
  feedbackObject: webhookData.feedback, // Keep original feedback object

  // Generate unique review ID
  reviewId: `REV-${webhookData.employeeId}-${Date.now()}`,

  // Timestamp
  processedAt: new Date().toISOString()
};

// Return the processed data
return { json: output };

// Get the OpenAI response from the new output structure
const openaiOutput = $input.item.json.output[0].content[0].text;

// Remove markdown code blocks if present
let cleanedResponse = openaiOutput
  .replace(/```json\s*/g, '')
  .replace(/```\s*/g, '')
  .trim();

// Parse the JSON
let aiSummary;
try {
  aiSummary = JSON.parse(cleanedResponse);
} catch (error) {
  // If parsing fails, try to extract JSON from text
  const jsonMatch = cleanedResponse.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    aiSummary = JSON.parse(jsonMatch[0]);
  } else {
    throw new Error('Could not parse AI response: ' + error.message);
  }
}

// Get previous node data (from the Process Employee Data node)
const previousData = $('Process Employee Data').item.json;

// Merge everything together
const output = {
  ...previousData, // All employee data from Code node

  // AI-generated content
  executiveSummary: aiSummary.executiveSummary,
  keyStrengths: aiSummary.keyStrengths,
  developmentAreas: aiSummary.developmentAreas,
  closingStatement: aiSummary.closingStatement,

  // Keep original AI response for reference
  aiResponseRaw: openaiOutput,

  // Format strengths and development areas as HTML lists for later use
  strengthsHTML: aiSummary.keyStrengths
    .map(strength => `<li>${strength}</li>`)
    .join(''),
  developmentHTML: aiSummary.developmentAreas
    .map(area => `<li>${area}</li>`)
    .join('')
};

return { json: output };

// Get all the data
const data = $input.item.json;

// Generate the complete HTML document
