// Recursive function to clean strings
function cleanString(str) {
  if (typeof str !== 'string') return str;
  // Remove everything from " ([" to the end, including the pattern
  return str.replace(/\s*\(\[.*?\]\(.*?\)\)/g, '').trim();
}

// Recursive function to process all values in an object/array
function cleanObject(obj) {
  if (Array.isArray(obj)) {
    return obj.map(item => cleanObject(item));
  } else if (obj !== null && typeof obj === 'object') {
    const cleaned = {};
    for (const key in obj) {
      cleaned[key] = cleanObject(obj[key]);
    }
    return cleaned;
  } else if (typeof obj === 'string') {
    return cleanString(obj);
  }
  return obj;
}

// Clean the entire data structure (handles the array wrapper)
const cleanedData = cleanObject(data);

return {
  json: cleanedData
};

