return items.map(item => {
  const data = item.json;

  // Navigate to the message output content
  const outputs = data.output || [];

  // Find the message type output
  const messageOutput = outputs.find(output => output.type === 'message');

  if (!messageOutput || !messageOutput.content) {
    return {
      json: {
        error: 'No message content found',
        searchTerms: []
      }
    };
  }

  // Get the text content
  const textContent = messageOutput.content.find(c => c.type === 'output_text');

  if (!textContent || !textContent.text) {
    return {
      json: {
        error: 'No text content found',
        searchTerms: []
      }
    };
  }

  const fullText = textContent.text;

  // Extract JSON from the text (look for { and } that contain "top_search_terms")
  let jsonText = '';
  let startIdx = fullText.indexOf('{');

  if (startIdx === -1) {
    return {
      json: {
        error: 'No JSON object found in text',
        searchTerms: []
      }
    };
  }

  // Find the matching closing brace
  let braceCount = 0;
  let endIdx = -1;

  for (let i = startIdx; i < fullText.length; i++) {
    if (fullText[i] === '{') braceCount++;
    if (fullText[i] === '}') braceCount--;

    if (braceCount === 0) {
      endIdx = i + 1;
      break;
    }
  }

  if (endIdx === -1) {
    return {
      json: {
        error: 'Could not find complete JSON object',
        searchTerms: []
      }
    };
  }

  jsonText = fullText.substring(startIdx, endIdx);

  // Parse the JSON text
  let parsedData;
  try {
    parsedData = JSON.parse(jsonText);
  } catch (e) {
    return {
      json: {
        error: 'Failed to parse JSON content',
        rawJson: jsonText,
        searchTerms: []
      }
    };
  }

  // Extract search terms
  const topSearchTerms = parsedData.top_search_terms || [];
  const searchTerms = topSearchTerms.map(termObj => {
    // Get the first key-value pair from each term object
    const key = Object.keys(termObj)[0];
    return termObj[key];
  });

  // Return the extracted search terms
  return {
    json: {
      searchTerms: searchTerms,
      allTerms: topSearchTerms
    }
  };
});

