  jsonlText = jsonlText.replace(/```jsonl?\n?/g, '').replace(/```\n?/g, '').trim();

  // Split by newlines and parse each line as a separate JSON object
  const lines = jsonlText.split('\n').filter(line => line.trim() !== '');

  const phrases = [];

  for (const line of lines) {
    try {
      const parsedLine = JSON.parse(line);
      // Extract the "phrase" field from each JSONL object
      if (parsedLine.phrase) {
        phrases.push(parsedLine.phrase);
      }
    } catch (e) {
      // Skip invalid lines
      console.error('Failed to parse line:', line, e);
    }
  }

  // Return the extracted phrases
  return {
    json: {
      phrases: phrases,
      model: data.model || null,
      phraseCount: phrases.length
    }
  };
});

