const response = $json;

let base64Image;

if (response.candidates && response.candidates[0]?.content?.parts) {
  const parts = response.candidates[0].content.parts;
  for (const part of parts) {
    if (part.inlineData?.data) {
      base64Image = part.inlineData.data;
      break;
    }
  }
}

if (!base64Image && response.predictions && response.predictions[0]) {
  base64Image = response.predictions[0].bytesBase64Encoded;
}

if (!base64Image) {
  throw new Error('No image found in response: ' + JSON.stringify(Object.keys(response)));
}

return [{
  json: {
    image_base64: base64Image,
    topic: $('Validate Input2').item.json.topic,
    scent_name: $('Validate Input2').item.json.scent_name,
    brand_name: $('Validate Input2').item.json.brand_name
  }
}];

const item = $input.first();

const bin = item?.binary?.data || item?.binary?.file || item?.binary?.image;

if (!bin?.data || bin.data.length < 50) {
  throw new Error("Reference image binary missing or empty");
}

const imageBase64 = String(bin.data).replace(/^data:.*;base64,/, "");
const mimeType = bin.mimeType || "image/jpeg";

const scent = String($("Validate Input2").item.json.scent_name || "").trim();
const brand = String($("Validate Input2").item.json.brand_name || "").trim();
const topic = String($("Validate Input2").item.json.topic || "").trim();

const safe = (s) => String(s || "").replace(/\r?\n|\r/g, " ").replace(/\s+/g, " ").trim();

const brandSafe = safe(brand);
const scentSafe = safe(scent);

const imagePrompt = safe(`
IDENTITY LOCK:
- Preserve EXACT bottle silhouette, proportions, cap shape, nozzle placement, edges/corners, and all engravings from the reference.
- Do NOT redesign the bottle. Do NOT change geometry. Do NOT change the label/engraved layout.

TEXT LOCK:
- Do NOT redraw, regenerate, or modify any text, logo, or typography.
- Keep the exact original text from the reference. If frost covers text, let it be partially obscured/blurred ‚Äî never altered.

STYLE TRANSFORM ONLY:
- Apply realistic frozen treatment on top of the SAME bottle: dense micro-frost crystals across the bottle, thicker rime on edges and cap top, frozen condensation streaks.
- Background and lighting can change.

SCENE:
- Dark bluish-grey studio background, cold stone matte surface with scattered ice crystals.
- Soft diffused key light + subtle rim light, crisp highlights, realistic reflections.
- Centered front view, no props, vertical 9:16.

Product: ${brandSafe} ${scentSafe}.
`);

const requestBody = {
  contents: [
    {
      parts: [
        { inline_data: { mime_type: mimeType, data: imageBase64 } },
        { text: imagePrompt }
      ]
    }
  ],
  generationConfig: {
    responseModalities: ["IMAGE"],
    imageConfig: { aspectRatio: "9:16" }
  }
};

return [{ json: { request_body: requestBody, brand_name: brandSafe, scent_name: scentSafe, topic } }];

const items = $input.all();

const valid = items.find(item => {
  const topic = (item.json.topic || '').trim();
  const imageUrl = (item.json.image_url || '').trim();
  const status = (item.json.status || '').toLowerCase().trim();

  return topic.length > 0 && 
         imageUrl.length > 0 && 
         !['done', 'processing', 'uploaded', 'error'].includes(status);
});

if (!valid) {
  return [];
}

const topic = valid.json.topic.trim();
const parts = topic.split('‚Äî').map(s => s.trim());

return [{
  json: {
    topic: topic,
    scent_name: parts[0] || topic,
    brand_name: parts[1] || '',
    reference_url: valid.json.image_url.trim(),
    row_number: valid.json.row_number || 2
  }
}];

const response = $input.first().json;
const topic = String($('Validate Input2').item.json.topic || '').trim();
const videoUrl = $('Check Video Status').item.json.data.response.resultUrls[0];

let parsed;
try {
  let content = response.message?.content || response.content || '';
  content = content.replace(/```json\s*/g, '').replace(/```\s*/g, '').trim();
  parsed = JSON.parse(content);
} catch (e) {
  parsed = {};
}

return [{
  json: {
    topic,
    video_url: videoUrl,
    youtube_title: parsed.youtube_title || parsed?.youtube?.title || topic.substring(0, 60),
    youtube_description: parsed.youtube_description || parsed?.youtube?.description || '',
    tiktok_caption: parsed.tiktok_caption || parsed?.tiktok?.caption || '',
    instagram_caption: parsed.instagram_caption || parsed?.instagram?.caption || '',
    pinterest_title: parsed.pinterest_title || parsed?.pinterest?.title || topic,
    pinterest_description: parsed.pinterest_description || parsed?.pinterest?.description || ''
  }
}];
