  item => item.json.Email && item.json.Email.trim() !== ''
);

if (realRows.length > 0) {
  return realRows.map(item => ({
    json: {
      ...item.json,   // Google Sheets row
      email,
      exists: true
    }
  }));
}

// No match in sheet
return [{
  json: {
    email,
    exists: false,
    matches: 0
  }
}];
