const data = $input.first().json;

// Parse OpenAI JSON responses
let titleVariations = [];
let seoDescription = '';
let seoTags = [];

try {
  // Parse title variations from OpenAI JSON response
  if (data.message && typeof data.message === 'string') {
    const parsed = JSON.parse(data.message);
    titleVariations = parsed.titles || [];
  } else if (data.titles) {
    titleVariations = data.titles;
  }
} catch (e) {
  titleVariations = [data.videoTitle || 'Untitled'];
}

// Parse description from OpenAI JSON response
try {
  if (data.seoDescription && typeof data.seoDescription === 'string') {
    const parsed = JSON.parse(data.seoDescription);
    seoDescription = parsed.description || data.videoDescription || '';
  } else if (data.description) {
    seoDescription = data.description;
  } else {
    seoDescription = data.videoDescription || '';
  }
} catch (e) {
  seoDescription = data.videoDescription || '';
}

// Parse tags from OpenAI JSON response
try {
  if (data.seoTags && typeof data.seoTags === 'string') {
    const parsed = JSON.parse(data.seoTags);
    seoTags = parsed.tags || [];
  } else if (data.tags && Array.isArray(data.tags)) {
    seoTags = data.tags;
  } else if (Array.isArray(data.seoTags)) {
    seoTags = data.seoTags;
  }
} catch (e) {
  seoTags = data.videoTags || [];
}

// Calculate SEO score
const titleScore = titleVariations.length >= 3 ? 30 : 0;
const descScore = seoDescription.length >= 150 ? 30 : 0;
const tagsScore = seoTags.length >= 10 ? 40 : seoTags.length * 4;
const seoScore = titleScore + descScore + tagsScore;

return [{
  json: {
    videoId: data.videoId,
    videoTitle: data.videoTitle,
    videoDescription: data.videoDescription,
    videoTags: data.videoTags,
    duration: data.duration,
    titleVariations,
    seoDescription,
    seoTags,
    seoScore,
    seoGrade: seoScore >= 80 ? 'A' : seoScore >= 60 ? 'B' : seoScore >= 40 ? 'C' : 'D'
  }
}];

const data = $input.first().json;

// Assign A/B test variant (50/50 split based on video ID hash)
const videoId = data.videoId || '';
const hash = videoId.split('').reduce((acc, char) => acc + char.charCodeAt(0), 0);
const variant = hash % 2 === 0 ? 'A' : 'B';

// Select title based on variant
const selectedTitle = variant === 'A' && data.titleVariations && data.titleVariations.length > 0 
  ? data.titleVariations[0] 
  : data.videoTitle;

return [{
  json: {
    ...data,
    abVariant: variant,
    selectedTitle: selectedTitle
  }
}];

const data = $input.first().json;

if (!data || !data.id) {
  return [{
    json: {
      error: 'No analytics data available',
      videoId: 'unknown',
      views: 0,
      likes: 0,
      comments: 0,
      duration: 'PT0S',
      timestamp: new Date().toISOString()
    }
  }];
}

return [{
  json: {
    videoId: data.id,
    views: parseInt(data.statistics?.viewCount) || 0,
    likes: parseInt(data.statistics?.likeCount) || 0,
    comments: parseInt(data.statistics?.commentCount) || 0,
    duration: data.contentDetails?.duration || 'PT0S',
    timestamp: new Date().toISOString()
  }
}];

const data = $input.first().json;

if (data.error) {
  return [{
    json: {
      ...data,
      engagementScore: 0,
      engagementGrade: 'N/A'
    }
  }];
}

// Calculate engagement rate
const views = data.views || 1;
const engagementScore = ((data.likes + data.comments) / views) * 100;

return [{
  json: {
    ...data,
    engagementScore: parseFloat(engagementScore.toFixed(2)),
    engagementGrade: engagementScore >= 5 ? 'High' : engagementScore >= 2 ? 'Medium' : 'Low'
  }
}];

const items = $input.all();
const videoData = items[0].json;
const competitorData = items.find(i => i.json.competitors)?.json || {};
const trendingData = items.find(i => i.json.trending)?.json || {};

return [{
  json: {
    videoId: videoData.videoId,
    channelId: videoData.channelId,
    videoTitle: videoData.videoTitle,
    videoDescription: videoData.videoDescription,
    videoTags: videoData.videoTags || [],
    viewCount: videoData.viewCount,
    duration: videoData.duration,
    competitors: competitorData.competitors || [],
    trending: trendingData.trending || [],
    competitorAvgViews: competitorData.avgViews || 0,
    trendingKeywords: trendingData.keywords || []
  }
}];

