const message = $input.item.json.message || '';
const cleaned = message.trim().toLowerCase();
const keywords = cleaned.split(' ').filter(w => w.length > 3);

return {
  json: {
    ...$input.item.json,
    cleanedQuery: cleaned,
    keywords,
    chatInput: message
  }
};

const response = $input.item.json.output || $input.item.json.text || '';
const sentiment = $input.item.json.sentiment || 'neutral';

// Calculate confidence based on multiple factors
const hasReferences = response.includes('http') || response.includes('documentation');
const responseLength = response.length;
const isSpecific = responseLength > 100 && (response.includes('step') || response.includes('follow') || response.includes('guide'));

// Base confidence calculation
let confidence = 0.5;

// Add points for references
if (hasReferences) confidence += 0.2;

// Add points for specificity
if (isSpecific) confidence += 0.15;

// Add points for response length (longer, more detailed responses)
if (responseLength > 200) confidence += 0.1;
else if (responseLength > 100) confidence += 0.05;

// Cap confidence at 0.95
confidence = Math.min(confidence, 0.95);

return {
  json: {
    ...$input.item.json,
    confidence,
    aiResponse: response,
    sentiment
  }
};

const raw = $input.item.json.message ?? '';
const message = String(raw).toLowerCase();

let category = 'general';

if (/\b(billing|payment)\b/.test(message)) category = 'billing';
else if (/\b(bug|error)\b/.test(message)) category = 'technical';
else if (/\b(account|login)\b/.test(message)) category = 'account';
else if (/\bintegration\b/.test(message)) category = 'integration';

return { json: { ...$input.item.json, category } };

const sessionId = $input.item.json.userId ?? 'default';

const conversationHistory = Array.isArray($input.item.json.conversationHistory)
  ? $input.item.json.conversationHistory
  : [];

return {
  json: {
    ...$input.item.json,
    sessionId,
    conversationHistory,
    isNewConversation: conversationHistory.length === 0
  }
};

// Fetch conversation history from memory store based on userId
const userId = $input.item.json.userId || 'default';
const sessionId = $input.item.json.sessionId || userId;

// In a real implementation, this would query Supabase or a memory store
// For now, we'll simulate fetching history from the workflow context
const conversationHistory = $input.item.json.conversationHistory || [];

// Add current message to history
const currentMessage = {
  role: 'user',
  content: $input.item.json.message,
  timestamp: $input.item.json.timestamp || new Date().toISOString()
};

const updatedHistory = [...conversationHistory, currentMessage];

// Keep only last 10 messages to avoid context overflow
const recentHistory = updatedHistory.slice(-10);

return {
  json: {
    ...$input.item.json,
    conversationHistory: recentHistory,
    historyCount: recentHistory.length,
    sessionId
  }
};
