// Extract HTML from Structured Output Parser
const item = $input.first().json;

// The HTML is at item.output.html
let htmlContent;

if (item.output && item.output.html) {
  htmlContent = item.output.html;
} else {
  throw new Error('No HTML found. Structure: ' + JSON.stringify(Object.keys(item)));
}

// Unescape the HTML (it comes with escaped quotes)
htmlContent = htmlContent
  .replace(/\\"/g, '"')
  .replace(/\\n/g, '\n')
  .trim();

// Create binary file
const htmlBuffer = Buffer.from(htmlContent, 'utf-8');
const base64Data = htmlBuffer.toString('base64');

return {
  json: {
    timestamp: new Date().toISOString()
  },
  binary: {
    data: {
      data: base64Data,
      mimeType: 'text/html',
      fileName: 'anonymized_cv.html',
      fileExtension: 'html'
    }
  }
};

// Prepare PDF binary data for Stirling PDF text extraction
const items = $input.all();
const results = [];

for (let i = 0; i < items.length; i++) {
  const item = items[i];

  // Ensure the binary data is properly formatted
  if (item.binary && item.binary.file_0) {
    results.push({
      json: {
        ...item.json,
        pageNumber: i + 1,
        fileName: item.binary.file_0.fileName || `page_${i + 1}.pdf`
      },
      binary: {
        file_0: item.binary.file_0
      }
    });
  }
}

return results;

// Extract text from Stirling PDF response
const items = $input.all();

return items.map(item => {
  let extractedText = '';

  console.log('Processing item:', item.json.pageNumber);
  console.log('Available keys:', Object.keys(item));

  // Method 1: Check if text is directly in json
  if (item.json.text) {
    extractedText = item.json.text;
    console.log('Found text in json.text');
  }
  // Method 2: Check if text is in response field
  else if (item.json.response) {
    extractedText = item.json.response;
    console.log('Found text in json.response');
  }
  // Method 3: Check if text is in output field  
  else if (item.json.output) {
    extractedText = item.json.output;
    console.log('Found text in json.output');
  }
  // Method 4: Check binary data (if it exists)
  else if (item.binary && item.binary.data) {
    console.log('Checking binary data...');
    console.log('Binary data type:', typeof item.binary.data);

    try {
      // If binary.data is already a Buffer
      if (Buffer.isBuffer(item.binary.data)) {
        extractedText = item.binary.data.toString('utf-8');
      }
      // If binary.data is a base64 string
      else if (typeof item.binary.data === 'string') {
        extractedText = Buffer.from(item.binary.data, 'base64').toString('utf-8');
      }
      // If binary.data is an object with 'data' property
      else if (item.binary.data.data) {
        extractedText = Buffer.from(item.binary.data.data, 'base64').toString('utf-8');
      }
      console.log('Extracted from binary');
    } catch (error) {
      console.log('Binary conversion error:', error.message);
    }
  }

  console.log(`Text length: ${extractedText.length}`);
  console.log(`First 200 chars: ${extractedText.substring(0, 200)}`);

  return {
    json: {
      text: extractedText,
      pageNumber: item.json.pageNumber || 0,
      filename: item.json.fileName || item.json.filename || 'unknown'
    }
  };
});

// Normalize binary field name to 'data'
const items = $input.all();

return items.map(item => {
  // If binary.data already exists, return as-is
  if (item.binary && item.binary.data) {
    return item;
  }

  // If binary exists but not as 'data', rename it
  if (item.binary) {
    const binaryKeys = Object.keys(item.binary);

    if (binaryKeys.length > 0) {
      const firstKey = binaryKeys[0]; // Get first binary field (file_0, UploadCV, etc.)

      return {
        json: item.json,
        binary: {
          data: item.binary[firstKey] // Rename to 'data'
        }
      };
    }
  }

  // If no binary at all, return unchanged
  return item;
})
