const ticket = $input.first().json;

const qrPayload = JSON.stringify({
  t: ticket.ticketId,
  e: ticket.eventId,
  n: ticket.participantName.substring(0, 20),
  ts: Date.now()
});

const encodedPayload = Buffer.from(qrPayload).toString('base64');

return [{
  json: {
    ...ticket,
    qrData: encodedPayload,
    qrContent: `https://event.company.com/checkin?code=${encodedPayload}`
  }
}];

