parameters.jsCode:
const input = $input.first().json;
const qrCode = input.body?.code || input.body?.qrCode || '';

if (!qrCode) {
  return [{ json: { valid: false, error: 'No QR code provided' } }];
}

try {
  const decoded = Buffer.from(qrCode, 'base64').toString('utf8');
  const payload = JSON.parse(decoded);

  return [{
    json: {
      valid: true,
      ticketId: payload.t,
      eventId: payload.e,
      participantName: payload.n,
      scanTime: new Date().toISOString()
    }
  }];
} catch (e) {
  return [{ json: { valid: false, error: 'Invalid QR code format' } }];
}

const qrData = $('Decode QR Code').first().json;
const ticketRecords = $input.all();

if (ticketRecords.length === 0) {
  return [{ json: { allowed: false, reason: 'Ticket not found', ticketId: qrData.ticketId } }];
}

const ticket = ticketRecords[0].json;

if (ticket['Checked In'] === 'yes') {
  return [{ json: { 
    allowed: false, 
    reason: 'Already checked in at ' + ticket['Check-in Time'],
    ticketId: qrData.ticketId,
    participantName: ticket.Name
  } }];
}

return [{ json: {
  allowed: true,
  ticketId: qrData.ticketId,
  eventId: ticket['Event ID'],
  participantName: ticket.Name,
  ticketType: ticket['Ticket Type'],
  isVIP: ticket['Ticket Type']?.toLowerCase() === 'vip',
  checkInTime: qrData.scanTime
} }];

const checkinData = $('Validate Check-in').first().json;

const mockAttendance = 245;
const capacity = 300;
const attendancePercent = (mockAttendance / capacity * 100).toFixed(1);

return [{
  json: {
    status: 'success',
    message: `Welcome, ${checkinData.participantName}!`,
    ticketId: checkinData.ticketId,
    ticketType: checkinData.ticketType,
    checkInTime: checkinData.checkInTime,
    attendanceCount: mockAttendance,
    capacityPercent: parseFloat(attendancePercent)
  }
}];
