// Let's use the simplest, most common way to get the data when running 'Once for All Items'
// which is usually $json, or, more safely, $input.

const inputData = $input.all();

// Step 1: Ensure we have an array of the file objects
const sentryFiles = Array.isArray(inputData) ? inputData : [];

// Step 2: Handle empty or invalid input
// Using standard JavaScript 'Array.isArray'
if (sentryFiles.length === 0) {
    return [{
        json: { status: 'failure', description: 'No files returned from Sentry for analysis.' }
    }];
}

// Step 3: Extract file names for easier checking
// The map function can be executed directly on sentryFiles

const fileNames = sentryFiles.map(file => file.json.name);

// Step 4: Check for dSYM (Priority Check)
const hasDSYM = fileNames.some(name => /\.dSYM$/i.test(name));

// Step 5: Check for ProGuard/Mapping (Fallback Check)
const hasProguard = fileNames.some(name => /proguard\.txt$/i.test(name));
const hasMapping = fileNames.some(name => /mapping\.txt$/i.test(name));

// Step 6: Determine final status based on logic
let status, description;

// Condition 1: dSYM exists (highest priority)
if (hasDSYM) {
    status = 'success';
    description = 'Success: Valid dSYM artifact found.';
} 
// Condition 2: dSYM does NOT exist, but BOTH ProGuard and Mapping exist
else if (hasProguard && hasMapping) {
    status = 'success';
    description = 'Success: dSYM not found, but ProGuard and Mapping.txt artifacts are complete.';
} 
// Condition 3: Failure
else {
    const missing = [];
    if (!hasProguard) missing.push('proguard.txt');
    if (!hasMapping) missing.push('mapping.txt');

    status = 'failure';
    description = `Failure: Neither dSYM was found, nor was the complete set of ProGuard artifacts. Missing: ${missing.join(' and ')}`;
}

// Step 7: Return the final result
return [{ json: { status, description } }];

