const items = $input.all();
const results = [];

for (const item of items) {
  const data = item.json;
  let bucket, key, eventType;

  if (data.Records && data.Records[0]) {
    const record = data.Records[0];
    bucket = record.s3?.bucket?.name;
    key = decodeURIComponent(record.s3?.object?.key?.replace(/\+/g, ' '));
    eventType = record.eventName;
  } else if (data.body) {
    bucket = data.body.bucket || 'video-uploads';
    key = data.body.key || data.body.video_key;
    eventType = 'manual';
  } else {
    bucket = data.bucket || 'video-uploads';
    key = data.key || 'sample-video.mp4';
    eventType = 'unknown';
  }

  const videoExtensions = ['.mp4', '.mov', '.avi', '.mkv', '.webm', '.m4v'];
  const ext = key ? '.' + key.split('.').pop().toLowerCase() : '';
  const isVideo = videoExtensions.includes(ext);
  const jobId = `job_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

  results.push({
    json: {
      job_id: jobId,
      bucket: bucket,
      key: key,
      s3_url: `s3://${bucket}/${key}`,
      https_url: `https://${bucket}.s3.amazonaws.com/${encodeURIComponent(key)}`,
      filename: key ? key.split('/').pop() : 'unknown',
      extension: ext,
      is_video: isVideo,
      event_type: eventType,
      started_at: new Date().toISOString()
    }
  });
}

return results;

const items = $input.all();
const results = [];

for (const item of items) {
  const data = item.json;
  const probeResult = data.result || data;
  const videoStream = probeResult.streams?.find(s => s.codec_type === 'video') || {};
  const audioStream = probeResult.streams?.find(s => s.codec_type === 'audio') || {};
  const format = probeResult.format || {};

  const duration = parseFloat(format.duration || videoStream.duration || 0);
  const durationFormatted = new Date(duration * 1000).toISOString().substr(11, 8);
  const thumbnailTimes = [0.1, 0.3, 0.5, 0.7, 0.9].map(p => Math.floor(duration * p));

  const width = parseInt(videoStream.width || 1920);
  const height = parseInt(videoStream.height || 1080);
  const needsTranscode = width > 1920 || !['h264', 'hevc'].includes(videoStream.codec_name);

  results.push({
    json: {
      ...data,
      width: width,
      height: height,
      resolution: `${width}x${height}`,
      aspect_ratio: (width / height).toFixed(2),
      duration_seconds: duration,
      duration_formatted: durationFormatted,
      video_codec: videoStream.codec_name || 'unknown',
      video_bitrate: parseInt(videoStream.bit_rate || 0),
      frame_rate: videoStream.r_frame_rate || '30/1',
      audio_codec: audioStream.codec_name || 'none',
      audio_channels: audioStream.channels || 0,
      file_size_bytes: parseInt(format.size || 0),
      file_size_mb: (parseInt(format.size || 0) / (1024 * 1024)).toFixed(2),
      format_name: format.format_name || 'unknown',
      thumbnail_times: thumbnailTimes,
      needs_transcode: needsTranscode,
      target_resolutions: ['1080p', '720p', '480p']
    }
  });
}

return results;

const items = $input.all();
const data = items[0].json;
const jobData = data.data?.[0] || data;
const jobId = jobData.job_id || 'unknown';

const cdnBase = 'https://cdn.example.com';
const expiresAt = new Date(Date.now() + 24 * 60 * 60 * 1000).toISOString();

const result = {
  job_id: jobId,
  status: 'completed',
  completed_at: new Date().toISOString(),
  original: {
    filename: jobData.filename,
    resolution: jobData.resolution,
    duration: jobData.duration_formatted,
    size_mb: jobData.file_size_mb
  },
  thumbnails: {
    large: `${cdnBase}/thumbnails/${jobId}/thumb_0_large.jpg`,
    medium: `${cdnBase}/thumbnails/${jobId}/thumb_0_medium.jpg`,
    small: `${cdnBase}/thumbnails/${jobId}/thumb_0_small.jpg`
  },
  preview_gif: `${cdnBase}/previews/${jobId}/preview.gif`,
  transcoded: {
    '1080p': `${cdnBase}/transcoded/${jobId}/video_1080p.mp4`,
    '720p': `${cdnBase}/transcoded/${jobId}/video_720p.mp4`,
    '480p': `${cdnBase}/transcoded/${jobId}/video_480p.mp4`
  },
  urls_expire_at: expiresAt
};

return [{ json: result }];
