parameters.jsCode:
// Create diagnostic log entry with comprehensive ticket information
const timestamp = new Date().toISOString();
const ticketData = $input.item.json;

// Extract ticket information
const ticketId = ticketData.ticketId || ticketData.id || 'UNKNOWN';
const classification = ticketData.classification || ticketData.category || 'Not classified';
const solution = ticketData.solution || ticketData.response || 'No solution provided';
const confidenceScore = ticketData.confidence || ticketData.confidenceScore || 0;
const aiModel = ticketData.model || 'OpenAI';

// Create diagnostic log object
const diagnosticLog = {
  timestamp: timestamp,
  ticketId: ticketId,
  classification: {
    category: classification,
    confidence: confidenceScore
  },
  solution: {
    details: solution,
    generatedBy: aiModel,
    confidence: confidenceScore
  },
  aiMetrics: {
    classificationConfidence: confidenceScore,
    solutionConfidence: ticketData.solutionConfidence || confidenceScore,
    processingTime: ticketData.processingTime || 'N/A'
  },
  metadata: {
    workflowId: $workflow.id,
    executionId: $execution.id,
    nodeId: $node.id
  }
};

// Return the diagnostic log along with original data
return {
  json: {
    ...ticketData,
    diagnosticLog: diagnosticLog,
    logCreatedAt: timestamp
  }
};

