// Get project data from first input
const projectData = $input.first().json;

// Get team profiles from second input
const teamProfiles = $input.last().json;

// Merge into a single object
const mergedData = {
  projectData: projectData,
  teamProfiles: teamProfiles
};

// Return the merged data
return [{ json: mergedData }];

// Fetch task progress from project management system and calculate completion metrics
const projectData = query;

// Parse the project data if it's a string
let data;
try {
  data = typeof projectData === 'string' ? JSON.parse(projectData) : projectData;
} catch (error) {
  return 'Error: Invalid project data format';
}

// Calculate progress metrics
const tasks = data.tasks || [];
const totalTasks = tasks.length;

if (totalTasks === 0) {
  return 'No tasks found in the project';
}

const completedTasks = tasks.filter(task => task.status === 'completed').length;
const inProgressTasks = tasks.filter(task => task.status === 'in_progress').length;
const notStartedTasks = tasks.filter(task => task.status === 'not_started').length;

const completionPercentage = ((completedTasks / totalTasks) * 100).toFixed(2);

// Calculate progress by assignee
const progressByAssignee = {};
tasks.forEach(task => {
  const assignee = task.assignee || 'Unassigned';
  if (!progressByAssignee[assignee]) {
    progressByAssignee[assignee] = {
      total: 0,
      completed: 0,
      inProgress: 0,
      notStarted: 0
    };
  }
  progressByAssignee[assignee].total++;
  if (task.status === 'completed') progressByAssignee[assignee].completed++;
  if (task.status === 'in_progress') progressByAssignee[assignee].inProgress++;
  if (task.status === 'not_started') progressByAssignee[assignee].notStarted++;
});

// Format the report
const report = {
  overallProgress: {
    totalTasks,
    completedTasks,
    inProgressTasks,
    notStartedTasks,
    completionPercentage: `${completionPercentage}%`
  },
  progressByAssignee
};

return JSON.stringify(report, null, 2);

// Detect tasks that are delayed or at risk of delay
// Input: query should contain task data with progress, deadlines, and planned schedule

const DELAY_THRESHOLD_DAYS = 2; // Tasks delayed by more than 2 days are flagged

try {
  const taskData = JSON.parse(query);
  const currentDate = new Date();
  const delayedTasks = [];
  const atRiskTasks = [];

  // Process each task to detect delays
  if (Array.isArray(taskData.tasks)) {
    taskData.tasks.forEach(task => {
      const deadline = new Date(task.deadline);
      const plannedCompletion = task.plannedCompletionPercentage || 100;
      const actualProgress = task.actualProgress || 0;
      const daysUntilDeadline = Math.ceil((deadline - currentDate) / (1000 * 60 * 60 * 24));

      // Calculate expected progress based on timeline
      const totalDuration = task.estimatedDuration || 1;
      const elapsedTime = task.elapsedTime || 0;
      const expectedProgress = (elapsedTime / totalDuration) * 100;

      // Check if task is delayed
      if (actualProgress < expectedProgress - 10) {
        const delayDays = Math.ceil((expectedProgress - actualProgress) / 100 * totalDuration);

        if (delayDays > DELAY_THRESHOLD_DAYS) {
          delayedTasks.push({
            taskId: task.id,
            taskName: task.name,
            assignedTo: task.assignedTo,
            expectedProgress: expectedProgress.toFixed(1),
            actualProgress: actualProgress,
            delayDays: delayDays,
            daysUntilDeadline: daysUntilDeadline,
            severity: delayDays > 5 ? 'high' : 'medium'
          });
        } else {
          atRiskTasks.push({
            taskId: task.id,
            taskName: task.name,
            assignedTo: task.assignedTo,
            expectedProgress: expectedProgress.toFixed(1),
            actualProgress: actualProgress,
            delayDays: delayDays,
            daysUntilDeadline: daysUntilDeadline,
            severity: 'low'
          });
        }
      }
    });
  }

  const result = {
    analysisDate: currentDate.toISOString(),
    delayThresholdDays: DELAY_THRESHOLD_DAYS,
    delayedTasks: delayedTasks,
    atRiskTasks: atRiskTasks,
    summary: {
      totalDelayed: delayedTasks.length,
      totalAtRisk: atRiskTasks.length,
      highSeverity: delayedTasks.filter(t => t.severity === 'high').length
    }
  };

  return JSON.stringify(result, null, 2);

} catch (error) {
  return JSON.stringify({
    error: 'Failed to analyze delays',
    message: error.message,
    delayedTasks: [],
    atRiskTasks: []
  });
}

// Extract the projectPlan object from the agent output
const items = $input.all();
const outputItems = [];

for (const item of items) {
  // The agent output should contain the projectPlan in the response
  const agentOutput = item.json;

  // Extract projectPlan from the agent's output
  let projectPlan;

  if (agentOutput.output) {
    // If output is a string, try to parse it as JSON
    if (typeof agentOutput.output === 'string') {
      try {
        projectPlan = JSON.parse(agentOutput.output);
      } catch (e) {
        projectPlan = agentOutput.output;
      }
    } else {
      projectPlan = agentOutput.output;
    }
  } else if (agentOutput.projectPlan) {
    projectPlan = agentOutput.projectPlan;
  } else {
    // If no specific structure, use the whole output
    projectPlan = agentOutput;
  }

  // Prepare data for delay checking
  outputItems.push({
    json: {
      projectPlan: projectPlan,
      hasDelays: false,
      delayedTasks: [],
      timestamp: new Date().toISOString()
    }
  });
}

return outputItems;

// Generate Comprehensive Milestone Report

// Get input data from previous nodes
const items = $input.all();

// Initialize report structure
const report = {
  reportDate: new Date().toISOString(),
  projectStatus: {
    totalTasks: 0,
    completedTasks: 0,
    inProgressTasks: 0,
    pendingTasks: 0,
    completionPercentage: 0
  },
  teamUtilization: [],
  delays: [],
  reallocations: [],
  milestones: [],
  summary: ""
};

// Process each item to extract relevant information
for (const item of items) {
  const data = item.json;

  // Extract task information if available
  if (data.tasks) {
    const tasks = Array.isArray(data.tasks) ? data.tasks : [data.tasks];

    report.projectStatus.totalTasks = tasks.length;

    tasks.forEach(task => {
      if (task.status === 'completed') {
        report.projectStatus.completedTasks++;
      } else if (task.status === 'in_progress') {
        report.projectStatus.inProgressTasks++;
      } else {
        report.projectStatus.pendingTasks++;
      }

      // Check for delays
      if (task.isDelayed || task.delayed) {
        report.delays.push({
          taskId: task.id || task.taskId,
          taskName: task.name || task.title,
          assignedTo: task.assignedTo || task.assignee,
          delayDays: task.delayDays || 0,
          reason: task.delayReason || 'Not specified'
        });
      }
    });
  }

  // Extract team utilization data
  if (data.teamMembers || data.team) {
    const team = data.teamMembers || data.team;
    const teamArray = Array.isArray(team) ? team : [team];

    teamArray.forEach(member => {
      report.teamUtilization.push({
        name: member.name || member.memberName,
        role: member.role || member.position,
        assignedTasks: member.assignedTasks || 0,
        completedTasks: member.completedTasks || 0,
        utilizationPercentage: member.utilization || 0,
        availability: member.availability || 'Unknown'
      });
    });
  }

  // Extract reallocation information
  if (data.reallocations) {
    const reallocations = Array.isArray(data.reallocations) ? data.reallocations : [data.reallocations];

    reallocations.forEach(realloc => {
      report.reallocations.push({
        taskId: realloc.taskId,
        taskName: realloc.taskName,
        fromMember: realloc.from || realloc.previousAssignee,
        toMember: realloc.to || realloc.newAssignee,
        reason: realloc.reason || 'Optimization',
        timestamp: realloc.timestamp || new Date().toISOString()
      });
    });
  }

  // Extract milestone information
  if (data.milestones) {
    const milestones = Array.isArray(data.milestones) ? data.milestones : [data.milestones];

    milestones.forEach(milestone => {
      report.milestones.push({
        name: milestone.name || milestone.title,
        dueDate: milestone.dueDate || milestone.deadline,
        status: milestone.status || 'pending',
        completionPercentage: milestone.completion || 0,
        dependencies: milestone.dependencies || []
      });
    });
  }
}

// Calculate completion percentage
if (report.projectStatus.totalTasks > 0) {
  report.projectStatus.completionPercentage = Math.round(
    (report.projectStatus.completedTasks / report.projectStatus.totalTasks) * 100
  );
}

// Generate summary
const delayCount = report.delays.length;
const reallocationCount = report.reallocations.length;
const avgUtilization = report.teamUtilization.length > 0 
  ? Math.round(report.teamUtilization.reduce((sum, member) => sum + member.utilizationPercentage, 0) / report.teamUtilization.length)
  : 0;

report.summary = `Project Status Report - ${new Date().toLocaleDateString()}\n\n` +
  `Overall Progress: ${report.projectStatus.completionPercentage}% complete\n` +
  `Tasks: ${report.projectStatus.completedTasks}/${report.projectStatus.totalTasks} completed, ` +
  `${report.projectStatus.inProgressTasks} in progress, ${report.projectStatus.pendingTasks} pending\n` +
  `Team Utilization: ${avgUtilization}% average\n` +
  `Delays: ${delayCount} task(s) delayed\n` +
  `Reallocations: ${reallocationCount} task(s) reallocated\n\n` +
  (delayCount > 0 ? `‚ö†Ô∏è Action Required: ${delayCount} delayed task(s) need attention\n` : '‚úÖ All tasks on track\n');

// Add recommendations
report.recommendations = [];

if (delayCount > 0) {
  report.recommendations.push('Review delayed tasks and consider resource reallocation');
}

if (avgUtilization > 90) {
  report.recommendations.push('Team utilization is high - consider adding resources or adjusting timelines');
} else if (avgUtilization < 50) {
  report.recommendations.push('Team utilization is low - consider optimizing task distribution');
}

if (report.projectStatus.completionPercentage < 30 && report.milestones.length > 0) {
  report.recommendations.push('Project progress is behind - review milestone deadlines');
}

// Return the comprehensive report
return [{ json: report }];
