// Aggregate all learning data and create comprehensive manager report
const items = $input.all();

// Initialize statistics
const stats = {
  totalEmployees: 0,
  trainingAssignments: {
    total: 0,
    byModule: {}
  },
  completionRates: {
    completed: 0,
    inProgress: 0,
    notStarted: 0,
    averageCompletionRate: 0
  },
  quizPerformance: {
    totalQuizzes: 0,
    averageScore: 0,
    passRate: 0,
    scores: []
  },
  learningPaths: {
    total: 0,
    byType: {}
  }
};

const employeeDetails = [];

// Process each item
for (const item of items) {
  const data = item.json;

  // Count employees
  if (data.employeeId) {
    stats.totalEmployees++;

    // Collect employee details
    employeeDetails.push({
      employeeId: data.employeeId,
      name: data.employeeName || 'Unknown',
      trainingModules: data.trainingModules || [],
      completionStatus: data.completionStatus || 'not_started',
      quizScore: data.quizScore || 0,
      learningPath: data.learningPath || 'Not assigned'
    });
  }

  // Training assignments
  if (data.trainingModules && Array.isArray(data.trainingModules)) {
    stats.trainingAssignments.total += data.trainingModules.length;
    data.trainingModules.forEach(module => {
      stats.trainingAssignments.byModule[module] = (stats.trainingAssignments.byModule[module] || 0) + 1;
    });
  }

  // Completion rates
  if (data.completionStatus) {
    if (data.completionStatus === 'completed') {
      stats.completionRates.completed++;
    } else if (data.completionStatus === 'in_progress') {
      stats.completionRates.inProgress++;
    } else {
      stats.completionRates.notStarted++;
    }
  }

  // Quiz performance
  if (data.quizScore !== undefined) {
    stats.quizPerformance.totalQuizzes++;
    stats.quizPerformance.scores.push(data.quizScore);
  }

  // Learning paths
  if (data.learningPath) {
    stats.learningPaths.total++;
    stats.learningPaths.byType[data.learningPath] = (stats.learningPaths.byType[data.learningPath] || 0) + 1;
  }
}

// Calculate averages
if (stats.totalEmployees > 0) {
  stats.completionRates.averageCompletionRate = Math.round((stats.completionRates.completed / stats.totalEmployees) * 100);
}

if (stats.quizPerformance.scores.length > 0) {
  const totalScore = stats.quizPerformance.scores.reduce((sum, score) => sum + score, 0);
  stats.quizPerformance.averageScore = Math.round(totalScore / stats.quizPerformance.scores.length);
  stats.quizPerformance.passRate = Math.round((stats.quizPerformance.scores.filter(score => score >= 70).length / stats.quizPerformance.scores.length) * 100);
}

// Generate report summary
const reportSummary = `
=== LEARNING MANAGEMENT REPORT ===
Generated: ${new Date().toISOString()}

OVERVIEW:
- Total Employees: ${stats.totalEmployees}
- Total Training Assignments: ${stats.trainingAssignments.total}
- Total Learning Paths Created: ${stats.learningPaths.total}

COMPLETION STATUS:
- Completed: ${stats.completionRates.completed} (${stats.completionRates.averageCompletionRate}%)
- In Progress: ${stats.completionRates.inProgress}
- Not Started: ${stats.completionRates.notStarted}

QUIZ PERFORMANCE:
- Total Quizzes Taken: ${stats.quizPerformance.totalQuizzes}
- Average Score: ${stats.quizPerformance.averageScore}%
- Pass Rate (‚â•70%): ${stats.quizPerformance.passRate}%

TRAINING MODULES DISTRIBUTION:
${Object.entries(stats.trainingAssignments.byModule).map(([module, count]) => `- ${module}: ${count} assignments`).join('\n')}

LEARNING PATHS DISTRIBUTION:
${Object.entries(stats.learningPaths.byType).map(([path, count]) => `- ${path}: ${count} employees`).join('\n')}
`;

// Return comprehensive report
return [
  {
    json: {
      reportDate: new Date().toISOString(),
      summary: reportSummary,
      statistics: stats,
      employeeDetails: employeeDetails,
      recommendations: [
        stats.completionRates.averageCompletionRate < 50 ? 'Low completion rate - consider sending additional reminders' : null,
        stats.quizPerformance.averageScore < 70 ? 'Average quiz score below passing - review training material quality' : null,
        stats.completionRates.notStarted > stats.totalEmployees * 0.3 ? 'High number of employees not started - increase engagement efforts' : null
      ].filter(r => r !== null)
    }
  }
];

