const request = $input.first().json;
const resource_id = request.params?.resourceId || request.query?.resourceId;
const headers = request.headers || {};

const payment_tx_hash = headers['x-payment'] || headers['X-Payment'];
const agent_id = headers['x-agent-id'] || headers['X-Agent-Id'];

const resource = SELLER_CONFIG.catalog[resource_id];

if (!resource) {
  return [{
    json: {
      route: "error_404",
      http_response: {
        statusCode: 404,
        message: "Resource not found",
        resource_id: resource_id,
        available: Object.keys(SELLER_CONFIG.catalog)
      }
    }
  }];
}

return [{
  json: {
    config: SELLER_CONFIG,
    resource: resource,
    request: {
      resource_id: resource_id,
      agent_id: agent_id,
      payment_tx_hash: payment_tx_hash,
      has_payment: !!payment_tx_hash
    },
    route: payment_tx_hash ? "verify_payment" : "return_402"
  }
}];

const data = $input.first().json;
const config = data.config;
const resource = data.resource;

const token_decimals = config.payment.token === "DAI" ? 18 : 6;
const amount = Math.floor(resource.price_usd * Math.pow(10, token_decimals));
const nonce = `nonce_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

const response_402 = {
  statusCode: 402,
  message: "Payment Required",
  protocol: "x402",

  payTo: config.merchant.wallet_address,
  amount: amount.toString(),
  token: config.payment.token,
  chain: config.payment.chain,
  priceUsd: resource.price_usd.toString(),
  nonce: nonce,

  resource: {
    id: resource.id,
    title: resource.title,
    preview: resource.preview
  },

  merchant: {
    name: config.merchant.name,
    email: config.merchant.email
  },

  instructions: [
    `1. Send ${amount} ${config.payment.token} to ${config.merchant.wallet_address}`,
    `2. Wait for confirmation`,
    `3. Retry with header: x-payment: {tx_hash}`
  ]
};

return [{
  json: {
    http_response: response_402
  }
}];

const request_data = $('1ï¸âƒ£ Parse Request').first().json;
  const verification = $input.first().json;
  const config = request_data.config;
  const resource = request_data.resource;

// Check if verification succeeded
  if (!verification.verified) {
    return [{
      json: {
        route: "validation_failed",
        http_response: {
          statusCode: 400,
          error: "Payment verification failed",
          verified: verification.verified,
          details: verification.error || "Payment not found"
        }
      }
    }];
  }

  const recipient_ok = verification.to_address.toLowerCase() === config.merchant.wallet_address.toLowerCase();
  if (!recipient_ok) {
    return [{
      json: {
        route: "validation_failed",
        http_response: {
          statusCode: 400,
          error: "Wrong recipient",
          expected: config.merchant.wallet_address,
          received: verification.to_address
        }
      }
    }];
  }

  const amount_ok = Math.abs(verification.amount_usd - resource.price_usd) < 0.01;
  if (!amount_ok) {
    return [{
      json: {
        route: "validation_failed",
        http_response: {
          statusCode: 400,
          error: "Wrong amount",
          expected_usd: resource.price_usd,
          received_usd: verification.amount_usd
        }
      }
    }];
  }

  return [{
    json: {
      ...request_data,
      verification: verification,
      route: "validation_passed"
    }
  }];

