const input = items[0].json.body || items[0].json;

// --- User Inputs ---
const searchTerm = (input.property_type || "").trim(); // e.g. "3BHK"
const city = (input.city || "").replace(/"/g, '\\"');
const budgetStr = (input.budget || "").toString().replace(/[‚Çπ,]/g, "");
const budget = parseInt(budgetStr, 10);

// --- Budget range (¬±5%) ---
const lowerLimit = Math.floor(budget * 0.95);
const upperLimit = Math.ceil(budget * 1.05);

const formula = `AND(
  FIND(LOWER("${searchTerm}"), LOWER({Title})) > 0,
  {Location} = "${city}",
  OR(
    VALUE(SUBSTITUTE(SUBSTITUTE({Price}, "‚Çπ", ""), ",", "")) <= ${budget},
    AND(
      VALUE(SUBSTITUTE(SUBSTITUTE({Price}, "‚Çπ", ""), ",", "")) >= ${lowerLimit},
      VALUE(SUBSTITUTE(SUBSTITUTE({Price}, "‚Çπ", ""), ",", "")) <= ${upperLimit}
    )
  )
)`;

// --- Return ---
return [{ json: { formula } }];

