// Get first input JSON
const data = $input.first().json;

// Formal, long, corporate Offer Letter
const htmlContent = `
<!doctype html>
<html lang="en">
<head>
  <meta charset="utf-8" />
  <title>Offer Letter</title>
  <style>
    body {
      font-family: "Times New Roman", serif;
      background: #fff;
      color: #000;
      padding: 60px;
      font-size: 13pt;
      line-height: 1.7;
    }
    .container {
      max-width: 800px;
      margin: auto;
    }
    header {
      text-align: center;
      margin-bottom: 40px;
    }
    header h1 {
      font-size: 20pt;
      margin: 0;
      text-transform: uppercase;
    }
    header p {
      margin: 4px 0;
      font-size: 11pt;
    }
    .date {
      text-align: right;
      margin-bottom: 30px;
    }
    .subject {
      font-weight: bold;
      margin: 20px 0;
      text-decoration: underline;
    }
    p {
      margin: 12px 0;
      text-align: justify;
    }
    .highlight {
      font-weight: bold;
    }
    .section-title {
      font-weight: bold;
      margin-top: 18px;
      text-decoration: underline;
    }
    .signature {
      margin-top: 60px;
    }
    .signature p {
      margin: 4px 0;
    }
    footer {
      margin-top: 80px;
      font-size: 10pt;
      color: #555;
      text-align: center;
    }
  </style>
</head>
<body>
  <div class="container">
    <header>
      <h1>infyOm Technologies</h1>
      <p>123 Business Street, City, State, PIN</p>
      <p>Email: user@example.com | Phone: +1234567890</p>
    </header>

    <div class="date">Date: ${new Date().toLocaleDateString()}</div>

    <p>To,<br>
    <span class="highlight">${data.Name || ''}</span><br>
    Email: ${data.Email || ''}<br>
    Phone: ${data.Phone || ''}</p>

    <div class="subject">Subject: Offer of Employment</div>

    <p>Dear <span class="highlight">${data.Name || ''}</span>,</p>

    <p>We are pleased to offer you employment with <span class="highlight">infyOm Technologies</span>. After a thorough evaluation of your qualifications, skills, and experience, we are confident in your ability to contribute significantly to our organization‚Äôs success. We are excited to welcome you on board.</p>

    <p>Your appointment is being made for the position assigned to you by our company. The total annual compensation will be <span class="highlight">${data["Offer Salary"] || ''}</span>. Additional benefits, allowances, and perquisites will be applicable as per company policy.</p>

    <div class="section-title">1. Position & Reporting</div>
    <p>You will be designated as <span class="highlight">${data.Profession || ''}</span> and will report to your designated manager or any other authority assigned by the company. Your duties and responsibilities will be as per the job description provided to you and may be modified by the management from time to time.</p>

    <div class="section-title">2. Probation Period</div>
    <p>You will be on probation for a period of six (6) months from your date of joining. Upon successful completion of this probationary period, your performance will be reviewed and your services may be confirmed in writing. During probation, your services may be terminated with one month‚Äôs notice from either side.</p>

    <div class="section-title">3. Working Hours</div>
    <p>Your normal working hours will be as per company policy, subject to modifications depending upon business requirements. You may be required to work beyond standard hours in order to meet project deadlines and organizational commitments.</p>

    <div class="section-title">4. Leave Policy</div>
    <p>You will be entitled to leave as per the leave policy of the company in force at the time. All leaves must be applied for and approved in advance by your reporting manager, except in cases of emergency.</p>

    <div class="section-title">5. Confidentiality & Non-Disclosure</div>
    <p>You shall maintain the highest standards of confidentiality with respect to all proprietary and confidential information of the company. You shall not, under any circumstances, disclose, use, or exploit any such information without prior written consent of the management.</p>

    <div class="section-title">6. Termination of Employment</div>
    <p>After confirmation, your employment may be terminated by either party by giving ninety (90) days written notice or payment of equivalent salary in lieu thereof. The company reserves the right to terminate employment immediately without notice in case of misconduct, breach of trust, or violation of company policies.</p>

    <p>We look forward to a mutually rewarding association and are confident that your skills and dedication will add immense value to our organization.</p>

    <p>Sincerely,</p>

    <div class="signature">
      <p><strong>HR Manager</strong></p>
      <p>InfyOm Technologies</p>
    </div>

    <footer>
      This is a computer-generated offer letter and does not require a physical signature.
    </footer>
  </div>
</body>
</html>
`;

// Convert HTML to binary for n8n
const buffer = Buffer.from(htmlContent, 'utf8');
const binaryData = {
  data: buffer.toString('base64'),
  mimeType: 'text/html',
  fileName: `Offer_Letter_${data["Candidate ID"] || 'Candidate'}.html`,
};

// Attach binary to first item
items[0].binary = { data: binaryData };

return items;
