return items.map(item => {
  const src = item.json || {};
  const p = src.properties || {};

  const domain =
    p["domain"]?.value ||
    p["domain"] ||
    p["website"]?.value ||
    p["website"] ||
    src.domain ||
    src.website ||
    "";

  const companyId =
    src.id ||
    src.objectId ||
    src["objectId"] ||
    src.companyId ||
    "";

  return {
    json: {
      companyId,
      domain
    }
  };
});
