const items = $input.all();
const zoneData = $('Split Zones').item.json;
const results = [];

for (const item of items) {
  const current = item.json;
  const temp = current.main?.temp || 20;
  const humidity = current.main?.humidity || 50;
  const clouds = current.clouds?.all || 0;
  const windSpeed = current.wind?.speed || 0;

  // Check forecast for rain in next 24 hours
  const forecastList = current.list || [];
  const next24h = forecastList.slice(0, 8);
  const rainExpected = next24h.some(f => 
    f.weather?.[0]?.main === 'Rain' || 
    f.weather?.[0]?.main === 'Drizzle' ||
    (f.rain && f.rain['3h'] > 0)
  );
  const totalExpectedRain = next24h.reduce((sum, f) => sum + (f.rain?.['3h'] || 0), 0);

  // Calculate evapotranspiration estimate (simplified Penman)
  const solarRadiation = (100 - clouds) / 100 * 20;
  const evapotranspiration = (0.0023 * (temp + 17.8) * solarRadiation * 0.5).toFixed(2);

  // Soil moisture simulation based on last watered
  const lastWatered = new Date(zoneData.lastWatered);
  const daysSinceWater = Math.floor((Date.now() - lastWatered) / (1000 * 60 * 60 * 24));
  const soilMoistureEstimate = Math.max(0, 100 - (daysSinceWater * 15) - (evapotranspiration * 5));

  // Plant water requirements
  const plantNeeds = {
    'flowers': { min: 40, ideal: 60, frequency: 2 },
    'vegetables': { min: 50, ideal: 70, frequency: 1 },
    'grass': { min: 30, ideal: 50, frequency: 3 },
    'shrubs': { min: 35, ideal: 55, frequency: 4 }
  };
  const needs = plantNeeds[zoneData.plantType] || plantNeeds['flowers'];

  // Decision logic
  let shouldWater = false;
  let urgency = 'none';
  let reason = '';
  let waterAmount = 0;

  if (rainExpected && totalExpectedRain > 5) {
    shouldWater = false;
    reason = `Rain expected (${totalExpectedRain.toFixed(1)}mm in 24h) - skip watering`;
  } else if (soilMoistureEstimate < needs.min) {
    shouldWater = true;
    urgency = 'high';
    waterAmount = Math.round((needs.ideal - soilMoistureEstimate) * 0.5);
    reason = `Soil moisture critically low (${soilMoistureEstimate.toFixed(0)}%)`;
  } else if (soilMoistureEstimate < needs.ideal && temp > 25) {
    shouldWater = true;
    urgency = 'medium';
    waterAmount = Math.round((needs.ideal - soilMoistureEstimate) * 0.3);
    reason = `Hot day (${temp}¬∞C) and moisture below ideal`;
  } else if (daysSinceWater >= needs.frequency && !rainExpected) {
    shouldWater = true;
    urgency = 'low';
    waterAmount = Math.round((needs.ideal - soilMoistureEstimate) * 0.2);
    reason = `Scheduled watering (${daysSinceWater} days since last)`;
  } else {
    reason = `Adequate moisture (${soilMoistureEstimate.toFixed(0)}%) - no action needed`;
  }

  // Best watering time
  let bestTime = '06:00';
  if (temp > 30) bestTime = '05:00';
  if (windSpeed > 5) bestTime = '19:00';

  results.push({
    json: {
      zone: zoneData.zone,
      plantType: zoneData.plantType,
      soilType: zoneData.soilType,
      currentConditions: {
        temperature: temp,
        humidity: humidity,
        clouds: clouds,
        windSpeed: windSpeed
      },
      analysis: {
        soilMoistureEstimate: parseFloat(soilMoistureEstimate.toFixed(1)),
        evapotranspiration: parseFloat(evapotranspiration),
        daysSinceWater: daysSinceWater,
        rainExpected: rainExpected,
        expectedRainfall: parseFloat(totalExpectedRain.toFixed(1))
      },
      decision: {
        shouldWater: shouldWater,
        urgency: urgency,
        waterAmount: waterAmount,
        bestTime: bestTime,
        reason: reason
      },
      timestamp: new Date().toISOString()
    }
  });
}

return results;

const zonesToWater = $input.all();
const allZones = $('Aggregate All Results').first().json.data;

const schedule = [];
let currentTime = 6 * 60; // Start at 6:00 AM in minutes

for (const item of zonesToWater) {
  const zone = item.json;
  const duration = Math.max(10, zone.decision.waterAmount * 2); // min 10 minutes

  const startHour = Math.floor(currentTime / 60);
  const startMin = currentTime % 60;
  const endTime = currentTime + duration;
  const endHour = Math.floor(endTime / 60);
  const endMin = endTime % 60;

  schedule.push({
    zone: zone.zone,
    plantType: zone.plantType,
    urgency: zone.decision.urgency,
    startTime: `${String(startHour).padStart(2, '0')}:${String(startMin).padStart(2, '0')}`,
    endTime: `${String(endHour).padStart(2, '0')}:${String(endMin).padStart(2, '0')}`,
    durationMinutes: duration,
    waterAmount: zone.decision.waterAmount,
    reason: zone.decision.reason
  });

  currentTime = endTime + 5; // 5 min gap between zones
}

const totalDuration = schedule.reduce((sum, s) => sum + s.durationMinutes, 0);
const totalWater = schedule.reduce((sum, s) => sum + s.waterAmount, 0);

// Build report
let report = `üå± **SMART IRRIGATION SCHEDULE**\n`;
report += `üìÖ ${new Date().toISOString().split('T')[0]}\n`;
report += `‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\n\n`;

if (schedule.length === 0) {
  report += `‚úÖ No irrigation needed today!\n\n`;
  report += `**Zone Status:**\n`;
  for (const z of allZones) {
    report += `‚Ä¢ ${z.zone}: ${z.decision.reason}\n`;
  }
} else {
  report += `**Today's Schedule (${schedule.length} zones):**\n\n`;
  for (const s of schedule) {
    const urgencyIcon = s.urgency === 'high' ? 'üî¥' : s.urgency === 'medium' ? 'üü°' : 'üü¢';
    report += `${urgencyIcon} **${s.zone}** (${s.plantType})\n`;
    report += `   ‚è∞ ${s.startTime} - ${s.endTime} (${s.durationMinutes} min)\n`;
    report += `   üíß ${s.waterAmount}L | ${s.reason}\n\n`;
  }
  report += `‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\n`;
  report += `üìä **Total:** ${totalDuration} min | ${totalWater}L estimated\n`;
}

return [{
  json: {
    schedule: schedule,
    summary: {
      totalZones: schedule.length,
      totalDuration: totalDuration,
      totalWater: totalWater,
      date: new Date().toISOString().split('T')[0]
    },
    allZoneStatus: allZones,
    report: report,
    hasIrrigation: schedule.length > 0
  }
}];
