const today = new Date();

const formatDate = (date) => {
  const year = date.getFullYear();
  const month = String(date.getMonth() + 1).padStart(2, '0');
  const day = String(date.getDate()).padStart(2, '0');
  return `${year}-${month}-${day}`;
};
const getDayOfWeek = (date) => {
  const days = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday", "Sunday"];
  return days[date.getDay()];
};

return [
  {
    date: formatDate(today),
    dayOfWeek: getDayOfWeek(today)
  }
];

const inputData = $input.all();
const records = inputData[0].json.result.records;

const today = new Date().toISOString().split('T')[0]; // YYYY-MM-DD

const filteredRecords = records.filter(record => record.state !== "draft" && record.state !== "cancel");

const result = filteredRecords
  .filter(record => record.expected_revealing_date === today)
  .map(record => ({ id: record.employee_id.id }));

return result;

