// Get all inputs
const itemsList = items.map(i => i.json);

// Pick machine data from ANY item (same for all items)
const baseData = {
  machine_id: itemsList[0]["machine id"],
  temperature: itemsList[0].temperature,
  vibration: itemsList[0].vibration,
  timestamp: itemsList[0].timestamp,
  jira_ticket: itemsList[0]["jira ticket"]
};

// Find first active technician
const activeTech = itemsList.find(i => i.technicianAvailable === "active");

if (activeTech) {
  // Technician available
  return [
    {
      json: {
        finalTechnicianAvailable: true,
        technicianId: activeTech["technician member id"],
        ...baseData
      }
    }
  ];
} else {
  // No technician available
  return [
    {
      json: {
        finalTechnicianAvailable: false,
        technicianId: null,
        ...baseData
      }
    }
  ];
}

