const norm = s => s.trim().toLowerCase();

// Build topic-to-category map
const topicToCategory = {};
for (const [category, topics] of Object.entries(categoryMap)) {
  for (const t of topics) {
    topicToCategory[norm(t)] = category;
  }
}

// Category priority (higher index = higher priority for tie-breaking)
const categoryPriority = [
  'Other',
  'Web Apps',
  'Mobile Apps',
  'Entertainment',
  'Social Media',
  'Health & Fitness',
  'Education',
  'Finance',
  'E-commerce',
