  'Developer Tools',
  'AI & Machine Learning'
];

function matchCategory(topic) {
  const normalized = norm(topic);

  // 1. Exact match
  if (topicToCategory[normalized]) {
    return topicToCategory[normalized];
  }

  // 2. Partial match (topic contains keyword or keyword contains topic)
  for (const [keyword, category] of Object.entries(topicToCategory)) {
    if (normalized.includes(keyword) || keyword.includes(normalized)) {
      return category;
    }
  }

  return null;
}

