const records = items.map(item => item.json);

// Helper: extract date (YYYY-MM-DD) from ISO timestamp
function extractDate(isoString) {
  return isoString ? isoString.split('T')[0] : 'Unknown';
}

// Group records by date
const grouped = {};

for (const record of records) {
  const date = extractDate(record.timestamp);
  if (!grouped[date]) {
    grouped[date] = {
      date,
      totalProcessed: 0,
      successful: 0,
      duplicates: 0,
      failures: 0,
      failureRate: 0,
      emailsSent: 0,
      emailFailures: 0
    };
  }

  const group = grouped[date];
  group.totalProcessed++;

  // Count success/duplicate/error
  if (record.status === 'success') group.successful++;
  else if (record.status === 'duplicate') group.duplicates++;
  else if (record.status === 'error') group.failures++;

  // Email sent tracking
  if (record.emailSent === 'yes') group.emailsSent++;
  else if (record.emailSent === 'failed') group.emailFailures++;
}

// Compute failure rate
for (const date in grouped) {
  const group = grouped[date];
  group.failureRate = group.totalProcessed
    ? ((group.failures / group.totalProcessed) * 100).toFixed(2) + '%'
    : '0%';
}

// Convert object to array for n8n output
return Object.values(grouped).map(summary => ({ json: summary }));

