const body = $input.first().json.body || $input.first().json;
const pr = body.pull_request || {};
return [{ json: { prNumber: pr.number, prTitle: pr.title, prBody: pr.body || '', author: pr.user?.login, repoOwner: body.repository?.owner?.login, repoName: body.repository?.name, additions: pr.additions, deletions: pr.deletions, changedFiles: pr.changed_files, diffUrl: pr.diff_url, htmlUrl: pr.html_url } }];

const input = $input.first().json;
const prData = $('Extract PR Data').first().json;
const diff = $('Fetch PR Diff').first().json.data || '';
const labels = prData.additions + prData.deletions > 200 ? ['size/L'] : ['size/S'];
return [{ json: { ...prData, diffPreview: diff.substring(0, 3000), suggestedLabels: labels } }];

const input = $input.first().json;
const prData = $('Analyze File Changes').first().json;
const review = { qualityScore: 85, summary: "AI Analysis Complete", approvalRecommendation: "approve" };
return [{ json: { ...prData, aiReview: review, finalLabels: prData.suggestedLabels, reviewComment: "AI reviewed your PR." } }];
