const input = $input.first().json;
let output = [];

// Sitemap Index
if (input.sitemapindex && input.sitemapindex.sitemap) {
    const sitemaps = Array.isArray(input.sitemapindex.sitemap) 
        ? input.sitemapindex.sitemap 
        : [input.sitemapindex.sitemap];

    output = sitemaps.map(s => ({
        url: s.loc,
        isSitemapIndex: true
    }));
} 
// Single sitemap
else if (input.urlset && input.urlset.url) {
    const urls = Array.isArray(input.urlset.url) 
        ? input.urlset.url 
        : [input.urlset.url];

    output = urls.map(u => ({
        urlData: u,
        isSitemapIndex: false
    }));
}

return output;

// Load previously submitted URLs from storage
const items = [];

for (const item of $input.all()) {
  const url = item.json.url;
  let shouldSubmit = true;
  let reason = 'new_url';

  try {
    // Check if URL was submitted before
    const storageKey = `indexed:${Buffer.from(url).toString('base64').substring(0, 50)}`;
    const result = await $execution.getWorkflowStaticData('global');

    if (result && result[storageKey]) {
      const lastSubmitted = new Date(result[storageKey]);
      const daysSinceSubmit = Math.floor((Date.now() - lastSubmitted.getTime()) / (1000 * 60 * 60 * 24));

      // Only resubmit if more than 7 days have passed
      if (daysSinceSubmit < 7) {
        shouldSubmit = false;
        reason = `submitted_${daysSinceSubmit}_days_ago`;
      } else {
        reason = 'resubmit_after_7_days';
      }
    }
  } catch (error) {
    // If storage fails, default to submitting
    reason = 'storage_error';
  }

  items.push({
    json: {
      ...item.json,
      shouldSubmit,
      reason
    }
  });
}

return items;

// Check if URL needs indexing based on Search Console response
const items = [];

for (const item of $input.all()) {
  let needsIndexing = true;
  let indexStatus = 'unknown';

  try {
    const response = item.json;

    // Check coverage state
    if (response.inspectionResult && response.inspectionResult.indexStatusResult) {
      const coverageState = response.inspectionResult.indexStatusResult.coverageState;
      indexStatus = coverageState;

      // If already indexed and no issues, skip
      if (coverageState === 'Submitted and indexed' || coverageState === 'Indexed, not submitted in sitemap') {
        needsIndexing = false;
      }
    }
  } catch (error) {
    // If check fails, default to indexing
    indexStatus = 'check_failed';
  }

  items.push({
    json: {
      url: item.json.url || item.json.inspectionResult?.inspectionUrl,
      needsIndexing,
      indexStatus,
      originalData: item.json
    }
  });
}

return items;
