body = body
  .replace(/<style[^>]*>[\s\S]*?<\/style>/gi, '')
  .replace(/<meta[^>]*>/gi, '')
  .replace(/<\/?html[^>]*>/gi, '')
  .replace(/<\/?head[^>]*>/gi, '')
  .replace(/<\/?body[^>]*>/gi, '')
  .trim();

// 3) strip unnecessary attributes 
body = body
  .replace(/\s(style|class|id|dir|lang|width|height|border|cellpadding|cellspacing)=(".*?"|'.*?'|[^\s>]+)/gi, '')
  .replace(/\s(data-[a-z0-9_-]+|aria-[a-z0-9_-]+)=(".*?"|'.*?'|[^\s>]+)/gi, '');

// 4) Peel off the span wrapper (repeat several times)
for (let i = 0; i < 5; i++) {
  body = body.replace(/<span[^>]*>([\s\S]*?)<\/span>/gi, '$1');
}

// 5) Remove empty paragraphs around <hr>
body = body
  .replace(/<p>\s*<\/p>/gi, '')
  .replace(/<hr>\s*/gi, '<hr>');

function decodeHtmlEntities(str) {
  if (typeof str !== 'string') return str;

  // numeric entities
