    '&Agrave;':'√Ä','&Aacute;':'√Å','&Acirc;':'√Ç','&Atilde;':'√É','&Auml;':'√Ñ','&Aring;':'√Ö','&AElig;':'√Ü','&Ccedil;':'√á',
