    '&eth;':'√∞','&ntilde;':'√±','&ograve;':'√≤','&oacute;':'√≥','&ocirc;':'√¥','&otilde;':'√µ','&ouml;':'√∂','&oslash;':'√∏',
    '&ugrave;':'√π','&uacute;':'√∫','&ucirc;':'√ª','&uuml;':'√º','&yacute;':'√Ω','&thorn;':'√æ','&yuml;':'√ø',
  };

  // decode named
  str = str.replace(/&[A-Za-z]+?;/g, (m) => (map[m] !== undefined ? map[m] : m));

